export const DESTINATION_DAY_THEMES: Record<string, Array<{ title: string; desc: string; schedule: string[]; image: string }>> = {
  paris: [
    { title: "Arrival & Le Marais Evening", desc: "Arrive at Charles de Gaulle and transfer to your boutique hotel. Explore Le Marais on foot — Paris’s most vibrant quarter with medieval architecture, independent boutiques, and the elegant Place des Vosges. Dinner at a traditional bistro followed by crêpes from a street-side stand.", schedule: ["Airport transfer to hotel", "Explore Le Marais on foot", "Place des Vosges", "Dinner at traditional bistro"], image: "https://images.unsplash.com/photo-1502602898657-3e91760cbb34?w=600&h=400&fit=crop&q=80" },
    { title: "Icons of Paris", desc: "Begin at the Louvre with a guided tour focusing on the must-sees — Mona Lisa, Winged Victory, Venus de Milo — without the overwhelm. Stroll through the Tuileries Garden to Place de la Concorde. Afternoon visit to the Musée d’Orsay for Impressionist masterpieces. Evening Seine river cruise with champagne as the city’s monuments glow.", schedule: ["Louvre Museum guided tour", "Tuileries Garden stroll", "Musée d’Orsay", "Evening Seine river cruise"], image: "https://images.unsplash.com/photo-1499856871958-5b9627545d1a?w=600&h=400&fit=crop&q=80" },
    { title: "Montmartre & Bohemian Paris", desc: "Morning ascent to Sacré-Cœur for panoramic city views. Wander the artist quarter of Place du Tertre, visit the Espace Dalí museum, and trace the footsteps of Amelie Poulain. Afternoon patisserie walking tour through Saint-Germain-des-Prés — sampling macarons, éclairs, and pain au chocolat from Paris’s finest bakers. Evening at a jazz club in the Latin Quarter.", schedule: ["Sacré-Cœur panoramic views", "Place du Tertre artist quarter", "Espace Dalí museum", "Patisserie tour in Saint-Germain", "Jazz club in Latin Quarter"], image: "https://images.unsplash.com/photo-1520939817895-060bdaf4fe1b?w=600&h=400&fit=crop&q=80" },
    { title: "Versailles Day Trip", desc: "Full-day excursion to the Palace of Versailles. Explore the Hall of Mirrors, the King’s Grand Apartments, and Marie Antoinette’s private estate in the Petit Trianon. Picnic lunch in the palace gardens before exploring the Grand Canal by rowboat. Return to Paris for a farewell dinner at a Michelin-recommended restaurant near the Champs-Élysées.", schedule: ["Palace of Versailles", "Hall of Mirrors", "Marie Antoinette’s estate", "Picnic in palace gardens", "Grand Canal rowboat", "Farewell dinner"], image: "https://images.unsplash.com/photo-1551410224-699683e15636?w=600&h=400&fit=crop&q=80" },
    { title: "Hidden Paris & Departure", desc: "Morning visit to the enchanting covered passages of Paris — Galerie Vivienne and Passage des Panoramas — for antique books and artisan chocolatiers. Final coffee at the iconic Café de Flore. Transfer to the airport.", schedule: ["Covered passages exploration", "Galerie Vivienne", "Café de Flore", "Transfer to airport"], image: "https://images.unsplash.com/photo-1550340499-a6c60fc8287c?w=600&h=400&fit=crop&q=80" },
  ],
  dubai: [
    { title: "Arrival & Downtown Dazzle", desc: "Touch down at Dubai International and transfer to your hotel. After settling in, head to the Dubai Mall for a late-afternoon stroll through the souk extension. As evening falls, secure a spot at the Dubai Fountain — the world’s largest choreographed fountain system — and watch the water dance to music against the illuminated Burj Khalifa. Dinner at a rooftop restaurant overlooking Downtown.", schedule: ["Airport transfer to hotel", "Dubai Mall souk extension", "Dubai Fountain show", "Rooftop dinner overlooking Downtown"], image: "https://images.unsplash.com/photo-1512453979798-5ea266f8880c?w=600&h=400&fit=crop&q=80" },
    { title: "Old Dubai & Cultural Immersion", desc: "Start the morning with a guided walking tour of Al Fahidi Historical Neighbourhood, exploring wind-tower architecture and hidden art galleries. Cross Dubai Creek on a traditional abra to the Deira spice and gold souks. Afternoon visit to the Dubai Museum housed in the 18th-century Al Fahidi Fort. End the day with a dhow cruise dinner along the Creek as the city lights shimmer on the water.", schedule: ["Al Fahidi Historical Neighbourhood", "Abra across Dubai Creek", "Deira spice and gold souks", "Dubai Museum", "Dhow cruise dinner"], image: "https://images.unsplash.com/photo-1582672060674-bc2bd808a8b5?w=600&h=400&fit=crop&q=80" },
    { title: "Desert Adventure", desc: "Morning at leisure by the hotel pool. After lunch, depart for a premium desert safari experience — dune bashing in vintage Land Rovers followed by a sunset camel ride across the red dunes. Arrive at a luxury Bedouin-style camp for a traditional Arabic barbecue under the stars, complete with henna painting, falconry displays, and live oud music.", schedule: ["Morning at leisure", "Premium desert safari", "Dune bashing in vintage Land Rovers", "Sunset camel ride", "Bedouin camp BBQ under the stars"], image: "https://images.unsplash.com/photo-1451337516015-6b6e9a44a8a3?w=600&h=400&fit=crop&q=80" },
    { title: "The Palm & Atlantis", desc: "Dedicate the day to Palm Jumeirah. Morning visit to Atlantis Aquaventure Waterpark and The Lost Chambers aquarium. Enjoy a leisurely beachfront lunch at a Palm restaurant. Afternoon options include jet-skiing, parasailing, or a scenic helicopter ride over the Palm. Evening at leisure to explore the luxury boutiques along the Palm boardwalk.", schedule: ["Palm Jumeirah exploration", "Atlantis Aquaventure Waterpark", "The Lost Chambers aquarium", "Beachfront lunch", "Water sports or helicopter ride", "Palm boardwalk"], image: "https://images.unsplash.com/photo-1580674684081-7617fbf3d745?w=600&h=400&fit=crop&q=80" },
    { title: "Sky-High & Shopping", desc: "Begin with sunrise tickets to At The Top — Burj Khalifa’s 148th-floor observation deck. Descend to explore the Dubai Mall, including the indoor aquarium and VR park. After lunch, head to Madinat Jumeirah for its canal-side souk and traditional architecture with views of the Burj Al Arab. Sunset cocktails at a beach bar overlooking the Arabian Gulf.", schedule: ["Burj Khalifa At The Top", "Dubai Mall aquarium and VR park", "Madinat Jumeirah souk", "Views of Burj Al Arab", "Sunset cocktails at beach bar"], image: "https://images.unsplash.com/photo-1582672060674-bc2bd808a8b5?w=600&h=400&fit=crop&q=80" },
    { title: "Abu Dhabi Day Excursion", desc: "Full-day trip to the capital. Visit the breathtaking Sheikh Zayed Grand Mosque — one of the world’s largest with 82 domes and a Swarovski crystal chandelier. Explore the Louvre Abu Dhabi, a masterpiece of modern architecture floating on water. Afternoon stop at Yas Island for those wanting Ferrari World or Yas Waterworld. Return to Dubai for a farewell dinner at a marina-side restaurant.", schedule: ["Sheikh Zayed Grand Mosque", "Louvre Abu Dhabi", "Yas Island", "Return to Dubai", "Farewell marina dinner"], image: "https://images.unsplash.com/photo-1518684079-3c830dcef090?w=600&h=400&fit=crop&q=80" },
    { title: "Leisure & Departure", desc: "Final morning for last-minute shopping at Mall of the Emirates or relaxation at the hotel spa. Depending on your flight time, enjoy a farewell brunch — Dubai’s famous Friday brunch culture is not to be missed. Transfer to Dubai International Airport.", schedule: ["Last-minute shopping or spa", "Farewell brunch", "Transfer to airport"], image: "https://images.unsplash.com/photo-1512453979798-5ea266f8880c?w=600&h=400&fit=crop&q=80" },
  ],
  bali: [
    { title: "Arrival & Seminyak Sunset", desc: "Arrive at Ngurah Rai International Airport and transfer to your resort in Seminyak. Unwind with a Balinese massage at the hotel spa. As evening approaches, head to a beachfront bar for cocktails and watch the famous Seminyak sunset paint the sky in vivid oranges and pinks.", schedule: ["Airport transfer to Seminyak resort", "Balinese massage at hotel spa", "Beachfront cocktails", "Seminyak sunset"], image: "https://images.unsplash.com/photo-1537996194471-e657df975ab4?w=600&h=400&fit=crop&q=80" },
    { title: "Ubud Cultural Heart", desc: "Drive north to Ubud, Bali’s cultural centre. Visit the Sacred Monkey Forest Sanctuary, then explore the Royal Palace and Ubud Art Market. Take a guided walk through the Tegallalang rice terraces and learn about the centuries-old subak irrigation system. Afternoon Balinese cooking class using fresh market ingredients. Stay overnight in Ubud.", schedule: ["Drive to Ubud", "Sacred Monkey Forest Sanctuary", "Royal Palace and Ubud Art Market", "Tegallalang rice terraces", "Balinese cooking class", "Overnight in Ubud"], image: "https://images.unsplash.com/photo-1555400038-63f5ba517a47?w=600&h=400&fit=crop&q=80" },
    { title: "Temples & Waterfalls", desc: "Early morning visit to Tirta Empul, the sacred water temple where locals perform purification rituals — participate if you wish. Continue to the hidden Tibumana waterfall for a refreshing swim in the jungle pool. Afternoon visit to the spectacular Gunung Kawi, an 11th-century temple carved into riverside cliffs.", schedule: ["Tirta Empul sacred water temple", "Purification ritual", "Tibumana waterfall swim", "Gunung Kawi riverside temple"], image: "https://images.unsplash.com/photo-1604999333679-b86d54738315?w=600&h=400&fit=crop&q=80" },
    { title: "Mount Batur Sunrise & Hot Springs", desc: "Pre-dawn departure for a guided trek up Mount Batur (1,717m). Watch the sunrise over the volcanic caldera and Lake Batur — an unforgettable experience. Descend and reward your efforts with a soak in natural hot springs overlooking the lake. Afternoon transfer south to the Uluwatu clifftop area.", schedule: ["Pre-dawn Mount Batur trek", "Sunrise over volcanic caldera", "Natural hot springs", "Transfer to Uluwatu area"], image: "https://images.unsplash.com/photo-1518548419970-58e3b4079ab2?w=600&h=400&fit=crop&q=80" },
    { title: "Uluwatu & Beaches", desc: "Morning surf lesson at Padang Padang Beach — Bali’s most photogenic cove. Spend the afternoon exploring hidden beaches along the Bukit Peninsula. As the sun sets, attend the iconic Kecak fire dance at Uluwatu Temple, performed on a cliff edge 70 metres above the crashing Indian Ocean waves.", schedule: ["Surf lesson at Padang Padang Beach", "Hidden beaches of Bukit Peninsula", "Kecak fire dance at Uluwatu Temple", "Sunset over Indian Ocean"], image: "https://images.unsplash.com/photo-1537996194471-e657df975ab4?w=600&h=400&fit=crop&q=80" },
    { title: "Nusa Penida Island", desc: "Full-day speedboat trip to Nusa Penida. Visit the jaw-dropping Kelingking Beach viewpoint (the famous T-Rex cliff), swim at Crystal Bay, and snorkel with manta rays at Manta Point. Enjoy a clifftop lunch with panoramic ocean views. Return to Bali in the late afternoon.", schedule: ["Speedboat to Nusa Penida", "Kelingking Beach viewpoint", "Crystal Bay swim", "Snorkel with manta rays", "Clifftop lunch", "Return to Bali"], image: "https://images.unsplash.com/photo-1573790387438-4da905039392?w=600&h=400&fit=crop&q=80" },
    { title: "Spa & Departure", desc: "Final morning for a traditional Balinese flower bath ritual and full spa treatment. Browse the boutiques of Seminyak for last-minute souvenirs — think hand-carved wooden crafts and batik textiles. Transfer to the airport.", schedule: ["Balinese flower bath ritual", "Full spa treatment", "Seminyak boutique shopping", "Transfer to airport"], image: "https://images.unsplash.com/photo-1537996194471-e657df975ab4?w=600&h=400&fit=crop&q=80" },
  ],
  maldives: [
    { title: "Arrival in Paradise", desc: "Arrive at Malé’s Velana International Airport and board a seaplane for a scenic 30-minute transfer over the atolls — a breathtaking introduction to the Maldives. Check into your overwater villa and spend the afternoon on your private deck, descending the stairs directly into the warm lagoon for your first swim.", schedule: ["Seaplane transfer over atolls", "Overwater villa check-in", "Private deck relaxation", "First lagoon swim"], image: "https://images.unsplash.com/photo-1514282401047-d79a71a590e8?w=600&h=400&fit=crop&q=80" },
    { title: "Snorkelling the House Reef", desc: "Wake up and spot fish through the glass floor of your villa. After breakfast, join a guided snorkelling session on the resort’s house reef, encountering sea turtles, reef sharks, and clouds of tropical fish. Afternoon spa treatment using coconut and frangipani oils. Sunset fishing trip on a traditional Maldivian dhoni boat.", schedule: ["Glass-floor fish spotting", "Guided house reef snorkelling", "Spa treatment", "Sunset dhoni fishing trip"], image: "https://images.unsplash.com/photo-1544551763-46a013bb70d5?w=600&h=400&fit=crop&q=80" },
    { title: "Private Sandbank Picnic", desc: "Speed boat to a deserted sandbank in the middle of the Indian Ocean. Enjoy a gourmet champagne picnic set up exclusively for you on pristine white sand surrounded by nothing but turquoise water and sky. Snorkel the surrounding reef, then return to the resort for dinner at the overwater restaurant.", schedule: ["Speed boat to deserted sandbank", "Gourmet champagne picnic", "Reef snorkelling", "Overwater restaurant dinner"], image: "https://images.unsplash.com/photo-1573843981267-be1999ff37cd?w=600&h=400&fit=crop&q=80" },
    { title: "Diving & Marine Life", desc: "Full-day diving excursion (or advanced snorkelling for non-divers) to a nearby channel where manta rays congregate. Witness these graceful giants glide through cleaning stations. Afternoon at leisure — kayaking, paddleboarding, or simply floating in the infinity pool. Night snorkelling to see the reef come alive after dark.", schedule: ["Diving or snorkelling excursion", "Manta ray encounter", "Kayaking or paddleboarding", "Infinity pool", "Night snorkelling"], image: "https://images.unsplash.com/photo-1540202404-a2f29016b523?w=600&h=400&fit=crop&q=80" },
    { title: "Dolphin Cruise & Spa Day", desc: "Morning dolphin-watching cruise — pods of spinner dolphins are common in Maldivian waters and often leap and spin alongside the boat. Return for a full spa day: couples massage in an overwater treatment room, body scrub, and aromatherapy. Evening dining experience on a private beach under the stars.", schedule: ["Dolphin-watching cruise", "Overwater couples massage", "Body scrub and aromatherapy", "Private beach dining under stars"], image: "https://images.unsplash.com/photo-1509233725247-49e657c54213?w=600&h=400&fit=crop&q=80" },
    { title: "Local Island Cultural Visit", desc: "Half-day excursion to a nearby inhabited island. Meet local families, visit the island school, see how traditional lacquerware crafts are made, and taste authentic Maldivian hedhikaa (short eats). Return to the resort for your final sunset — savoured from your villa deck with a cocktail.", schedule: ["Local island excursion", "Visit island school", "Traditional lacquerware crafts", "Maldivian hedhikaa tasting", "Final sunset from villa deck"], image: "https://images.unsplash.com/photo-1514282401047-d79a71a590e8?w=600&h=400&fit=crop&q=80" },
    { title: "Sunrise Yoga & Departure", desc: "Early morning yoga session on a floating platform over the lagoon as the sun rises. Enjoy a leisurely final breakfast. Seaplane transfer back to Malé for your departing flight.", schedule: ["Sunrise yoga on floating platform", "Final breakfast", "Seaplane transfer to Malé"], image: "https://images.unsplash.com/photo-1544551763-46a013bb70d5?w=600&h=400&fit=crop&q=80" },
  ],
  bangkok: [
    { title: "Arrival & River Impressions", desc: "Land at Suvarnabhumi Airport and transfer via express rail to your riverside hotel. Take a sunset long-tail boat ride along the Chao Phraya River past illuminated temples and palaces. Dinner at a riverside restaurant sampling classic pad thai and tom yum.", schedule: ["Airport transfer via express rail", "Sunset long-tail boat ride", "Illuminated temples along Chao Phraya", "Riverside dinner"], image: "https://images.unsplash.com/photo-1563492065599-3520f775eeed?w=600&h=400&fit=crop&q=80" },
    { title: "Grand Palace & Temple Trail", desc: "Early morning visit to the Grand Palace and Wat Phra Kaew (Temple of the Emerald Buddha). Continue to Wat Pho, home of the colossal Reclining Buddha and Thailand’s oldest massage school — enjoy a traditional Thai massage here. Cross the river by ferry to the stunning Wat Arun for sunset views over the Bangkok skyline.", schedule: ["Grand Palace and Wat Phra Kaew", "Wat Pho and Reclining Buddha", "Traditional Thai massage", "Ferry to Wat Arun", "Sunset over Bangkok"], image: "https://images.unsplash.com/photo-1563492065599-3520f775eeed?w=600&h=400&fit=crop&q=80" },
    { title: "Floating Markets & Street Food", desc: "Pre-dawn departure to Damnoen Saduak floating market — paddle through canals lined with vendors selling fresh produce, noodles, and handcrafts from wooden boats. Return to Bangkok for an afternoon street food walking tour through Chinatown’s Yaowarat Road — arguably the world’s best street food mile. Evening Muay Thai boxing at Rajadamnern Stadium.", schedule: ["Damnoen Saduak floating market", "Canal paddling with vendors", "Chinatown Yaowarat Road food tour", "Muay Thai at Rajadamnern Stadium"], image: "https://images.unsplash.com/photo-1552465011-b4e21bf6e79a?w=600&h=400&fit=crop&q=80" },
    { title: "Modern Bangkok & Rooftops", desc: "Morning shopping at Chatuchak Weekend Market — over 8,000 stalls across 35 acres. Explore the contemporary side of the city at ICONSIAM’s floating market and art galleries. Afternoon Thai cooking class in a local home. As night falls, ascend to one of Bangkok’s famous rooftop bars for cocktails with panoramic views of the glittering skyline.", schedule: ["Chatuchak Weekend Market", "ICONSIAM floating market", "Thai cooking class", "Rooftop bar cocktails"], image: "https://images.unsplash.com/photo-1559592413-7cec4d0cae2b?w=600&h=400&fit=crop&q=80" },
    { title: "Jim Thompson House & Departure", desc: "Morning visit to the Jim Thompson House — a stunning example of traditional Thai architecture filled with Southeast Asian art. Final stroll through the Siam area for last-minute shopping. Transfer to the airport.", schedule: ["Jim Thompson House", "Siam area shopping", "Transfer to airport"], image: "https://images.unsplash.com/photo-1506665531195-3566af2b4dfa?w=600&h=400&fit=crop&q=80" },
  ],
  santorini: [
    { title: "Arrival & Fira First Impressions", desc: "Arrive at Santorini’s Thira airport and transfer to your clifftop hotel. Spend the afternoon exploring Fira’s winding marble streets, browsing local jewellery shops and art galleries. Watch the sun sink into the caldera from a cocktail bar perched on the cliff edge.", schedule: ["Airport transfer to clifftop hotel", "Explore Fira’s marble streets", "Local jewellery and art galleries", "Caldera sunset cocktails"], image: "https://images.unsplash.com/photo-1570077188670-e3a8d69ac5ff?w=600&h=400&fit=crop&q=80" },
    { title: "Caldera Hiking Trail", desc: "Embark on the famous Fira-to-Oia hiking trail (approximately 10km). The path winds along the caldera rim through the villages of Firostefani and Imerovigli, offering uninterrupted panoramic views. Arrive in Oia for a late lunch at a cliffside taverna, then explore the village’s galleries and the famous blue domes before the sunset crowds arrive.", schedule: ["Fira-to-Oia caldera hike", "Firostefani and Imerovigli villages", "Late lunch in Oia", "Blue domes and galleries", "Oia sunset"], image: "https://images.unsplash.com/photo-1613395877344-13d4a8e0d49e?w=600&h=400&fit=crop&q=80" },
    { title: "Volcanic Excursion & Hot Springs", desc: "Board a traditional kaiki boat for a half-day excursion to the volcanic islets. Hike to the active crater of Nea Kameni and feel the heat underfoot, then swim in the natural hot springs of Palea Kameni where sulphurous warm water meets the cool Aegean. Afternoon at leisure in Fira, with an evening visit to a traditional Greek taverna.", schedule: ["Kaiki boat to volcanic islets", "Nea Kameni crater hike", "Hot springs at Palea Kameni", "Afternoon at leisure in Fira", "Traditional Greek taverna"], image: "https://images.unsplash.com/photo-1580502304784-8985b7eb7260?w=600&h=400&fit=crop&q=80" },
    { title: "Wine & Gastronomy", desc: "Santorini’s volcanic soil produces extraordinary wines. Spend the morning on a guided tour of three boutique wineries, tasting Assyrtiko and Vinsanto varieties aged in volcanic earth. Follow with a cooking class at a local home, learning to prepare fava dip, tomatokeftedes, and fresh seafood. Enjoy the meal you’ve prepared for lunch, then relax at a black sand beach.", schedule: ["Guided tour of three wineries", "Assyrtiko and Vinsanto tasting", "Cooking class at local home", "Lunch from your cooking class", "Black sand beach"], image: "https://images.unsplash.com/photo-1601581875309-fafbf2d3ed3a?w=600&h=400&fit=crop&q=80" },
    { title: "Beach Day & Ancient Akrotiri", desc: "Morning at the stunning Red Beach, tucked beneath dramatic crimson cliffs. After a swim, visit the archaeological site of Akrotiri — a remarkably preserved Minoan settlement buried by volcanic eruption in 1600 BC, often called the ‘Greek Pompeii’. Afternoon at Perissa’s black volcanic sand beach with beachside tavernas and cocktail bars.", schedule: ["Red Beach morning swim", "Akrotiri archaeological site", "Perissa black sand beach", "Beachside tavernas"], image: "https://images.unsplash.com/photo-1533105079780-92b9be482077?w=600&h=400&fit=crop&q=80" },
    { title: "Catamaran Sunset Cruise", desc: "Leisure morning for shopping or spa treatments. Mid-afternoon, board a luxury catamaran for a five-hour cruise around the caldera. Sail past the Indian Rocks, White Beach, and the lighthouse. Swim and snorkel in secluded coves, enjoy a barbecue dinner on deck, and watch the legendary Santorini sunset from the water — far from the Oia crowds.", schedule: ["Morning at leisure", "Luxury catamaran cruise", "Sail past Indian Rocks and lighthouse", "Swim and snorkel in secluded coves", "BBQ dinner on deck", "Sunset from the water"], image: "https://images.unsplash.com/photo-1504512485720-7d83a16ee930?w=600&h=400&fit=crop&q=80" },
    { title: "Pyrgos Village & Departure", desc: "Visit the hilltop village of Pyrgos, the highest point on the island with 360-degree views and the medieval Kasteli fortress. Browse artisan pottery workshops and enjoy a final Greek coffee. Transfer to the airport for departure.", schedule: ["Pyrgos hilltop village", "Kasteli fortress", "Artisan pottery workshops", "Final Greek coffee", "Transfer to airport"], image: "https://images.unsplash.com/photo-1570077188670-e3a8d69ac5ff?w=600&h=400&fit=crop&q=80" },
  ],
  rome: [
    { title: "Arrival & Trastevere Evening", desc: "Arrive at Fiumicino Airport and transfer to your hotel near the Piazza Navona. Evening stroll through Trastevere — Rome’s most charming neighbourhood with cobblestone streets, ivy-draped buildings, and lively trattorias. Dinner at a family-run restaurant serving cacio e pepe and Roman-style artichokes.", schedule: ["Airport transfer to hotel", "Evening stroll through Trastevere", "Dinner at family-run trattoria"], image: "https://images.unsplash.com/photo-1552832230-c0197dd311b5?w=600&h=400&fit=crop&q=80" },
    { title: "Ancient Rome", desc: "Early access visit to the Colosseum, including the underground chambers where gladiators once waited. Continue through the Roman Forum and up to Palatine Hill for sweeping views over the ancient city. Afternoon at the Pantheon and a gelato stop at one of Rome’s artisan gelaterias. Evening passeggiata along Via dei Fori Imperiali as the ruins glow under floodlights.", schedule: ["Colosseum early access with underground", "Roman Forum", "Palatine Hill", "Pantheon", "Artisan gelato", "Evening passeggiata"], image: "https://images.unsplash.com/photo-1552432552-06c0b0a94dda?w=600&h=400&fit=crop&q=80" },
    { title: "Vatican & Sacred Art", desc: "Guided tour of the Vatican Museums with early entry, culminating in the Sistine Chapel before the crowds. Visit St Peter’s Basilica and climb to the dome for a panoramic view over Rome. Afternoon exploring Castel Sant’Angelo and the surrounding Borgo neighbourhood. Dinner in the charming Prati district.", schedule: ["Vatican Museums early entry", "Sistine Chapel", "St Peter’s Basilica dome climb", "Castel Sant’Angelo", "Dinner in Prati district"], image: "https://images.unsplash.com/photo-1531572753322-ad063cecc140?w=600&h=400&fit=crop&q=80" },
    { title: "Baroque Rome & Hidden Gems", desc: "Morning visit to the Borghese Gallery (reservation essential) to see Bernini’s sculptures and Caravaggio’s paintings. Stroll through the Villa Borghese gardens. Afternoon treasure hunt through Rome’s piazzas — throw a coin at the Trevi Fountain, climb the Spanish Steps, and find the secret Aventine Keyhole with its perfectly framed view of St Peter’s. Evening food tour through the Jewish Ghetto neighbourhood.", schedule: ["Borghese Gallery", "Villa Borghese gardens", "Trevi Fountain", "Spanish Steps", "Aventine Keyhole", "Jewish Ghetto food tour"], image: "https://images.unsplash.com/photo-1529260830199-42c24126f198?w=600&h=400&fit=crop&q=80" },
    { title: "Appian Way & Departure", desc: "Morning bicycle ride along the Via Appia Antica — the ancient Roman road lined with pine trees, ruins, and catacombs. Visit the Catacombs of San Callisto for a glimpse of early Christian Rome. Final espresso at a piazza café before transferring to the airport.", schedule: ["Via Appia Antica bicycle ride", "Catacombs of San Callisto", "Final piazza espresso", "Transfer to airport"], image: "https://images.unsplash.com/photo-1515542622106-78bda8ba0e5b?w=600&h=400&fit=crop&q=80" },
  ],
  barcelona: [
    { title: "Arrival & Gothic Quarter", desc: "Arrive at El Prat Airport and transfer to your hotel in the Eixample district. Walk the Gothic Quarter’s medieval labyrinth — the Cathedral, Plaça del Rei, and hidden Roman ruins. Tapas dinner hopping between bodegas along Carrer de Blai.", schedule: ["Airport transfer to Eixample hotel", "Gothic Quarter exploration", "Cathedral and Plaça del Rei", "Tapas hopping on Carrer de Blai"], image: "https://images.unsplash.com/photo-1583422409516-2895a77efded?w=600&h=400&fit=crop&q=80" },
    { title: "Gaudí Masterpieces", desc: "Morning guided tour of the Sagrada Família — Gaudí’s unfinished masterpiece, with its forest-like interior columns and kaleidoscope stained glass. Continue to Park Güell for mosaic terraces and city views. Afternoon visit to Casa Batlló on Passeig de Gràcia for Gaudí’s fantastical residential architecture. Evening at a rooftop bar overlooking the Sagrada Família lit up at night.", schedule: ["Sagrada Família guided tour", "Park Güell mosaics", "Casa Batlló on Passeig de Gràcia", "Rooftop bar with Sagrada Família views"], image: "https://images.unsplash.com/photo-1562883676-8c7feb83f09b?w=600&h=400&fit=crop&q=80" },
    { title: "Montjuïc & El Born", desc: "Cable car up to Montjuïc for panoramic views, then explore the Fundació Joan Miró gallery and the Olympic Stadium. Descend to the vibrant El Born neighbourhood for boutique shopping and the Picasso Museum. Evening at the Magic Fountain light and music show (seasonal).", schedule: ["Montjuïc cable car", "Fundació Joan Miró", "Olympic Stadium", "El Born and Picasso Museum", "Magic Fountain show"], image: "https://images.unsplash.com/photo-1539037116277-4db20889f2d4?w=600&h=400&fit=crop&q=80" },
    { title: "Food, Wine & Beach", desc: "Morning visit to La Boqueria market — one of Europe’s finest food markets. Join a cooking class to prepare paella and sangria. Afternoon relaxation on Barceloneta Beach or stand-up paddleboarding. Sunset seafood dinner at a chiringuito (beach bar) with feet in the sand.", schedule: ["La Boqueria market", "Paella and sangria cooking class", "Barceloneta Beach", "Sunset chiringuito dinner"], image: "https://images.unsplash.com/photo-1579282240050-352db0a14c21?w=600&h=400&fit=crop&q=80" },
    { title: "Montserrat & Departure", desc: "Half-day excursion to Montserrat, the stunning mountain monastery an hour from Barcelona. Ride the funicular to the summit for breathtaking views. Listen to the Escolania boys’ choir (schedule permitting). Return to the city and transfer to the airport.", schedule: ["Montserrat mountain monastery", "Funicular to summit", "Escolania boys’ choir", "Transfer to airport"], image: "https://images.unsplash.com/photo-1464790719320-516ecd75af6c?w=600&h=400&fit=crop&q=80" },
  ],
  london: [
    { title: "Arrival & South Bank Stroll", desc: "Arrive at Heathrow and transfer to your hotel. Head to the South Bank for an evening walk along the Thames — the London Eye, the brutalist beauty of the Southbank Centre, and the illuminated Houses of Parliament across the water set the scene. Dinner at Borough Market's evening restaurants or a gastropub in Southwark.", schedule: ["Airport transfer to hotel", "South Bank evening walk", "London Eye and Southbank Centre", "Dinner in Southwark"], image: "https://images.unsplash.com/photo-1513635269975-59663e0ac1ad?w=600&h=400&fit=crop&q=80" },
    { title: "Royal London & Westminster", desc: "Morning at Buckingham Palace for the Changing of the Guard ceremony. Walk through St James's Park to Westminster Abbey, then along Whitehall past Downing Street to Trafalgar Square. Afternoon in the National Gallery — it's free and staggering. Cross to Covent Garden for street performers and a pre-theatre dinner, then catch a West End show.", schedule: ["Buckingham Palace Changing of the Guard", "St James's Park", "Westminster Abbey", "National Gallery", "Covent Garden", "West End show"], image: "https://images.unsplash.com/photo-1529655683826-aba9b3e77383?w=600&h=400&fit=crop&q=80" },
    { title: "The City & East London", desc: "Start at the Tower of London for the Crown Jewels and Beefeater tour. Walk across Tower Bridge, then explore the vibrant street art and vintage markets of Shoreditch and Brick Lane. Afternoon at the Sky Garden for free panoramic views. Evening in a Soho cocktail bar followed by dinner in Chinatown.", schedule: ["Tower of London Crown Jewels", "Tower Bridge", "Shoreditch and Brick Lane", "Sky Garden panoramic views", "Soho cocktail bar", "Chinatown dinner"], image: "https://images.unsplash.com/photo-1526129318478-62ed807ebdf9?w=600&h=400&fit=crop&q=80" },
    { title: "Museums & Royal Parks", desc: "Morning in South Kensington's museum quarter — the Natural History Museum's Hintze Hall alone is worth the visit. Stroll through Hyde Park to Kensington Palace. Afternoon shopping on Oxford Street or the boutiques of Marylebone High Street. Evening cruise on the Thames from Westminster to Greenwich, watching the city lights come alive.", schedule: ["Natural History Museum", "Hyde Park to Kensington Palace", "Oxford Street or Marylebone shopping", "Thames evening cruise"], image: "https://images.unsplash.com/photo-1486299267070-83823f5448dd?w=600&h=400&fit=crop&q=80" },
    { title: "Notting Hill & Departure", desc: "Morning exploring Notting Hill — Portobello Road Market's antiques and colourful townhouses. Final full English breakfast or brunch at a Notting Hill café. Transfer to the airport.", schedule: ["Notting Hill exploration", "Portobello Road Market", "Final brunch", "Transfer to airport"], image: "https://images.unsplash.com/photo-1533929736458-ca588d08c8be?w=600&h=400&fit=crop&q=80" },
  ],
  "new york": [
    { title: "Arrival & Times Square Energy", desc: "Land at JFK and transfer to your Manhattan hotel. Evening walk through the electric chaos of Times Square — the neon, the crowds, the sheer vertical scale of it all. Late dinner at a classic New York diner or a Hell's Kitchen restaurant.", schedule: ["Airport transfer to Manhattan", "Times Square evening walk", "Dinner in Hell's Kitchen"], image: "https://images.unsplash.com/photo-1496442226666-8d4d0e62e6e9?w=600&h=400&fit=crop&q=80" },
    { title: "Icons of Manhattan", desc: "Start at the Statue of Liberty and Ellis Island ferry (book early). Return to Lower Manhattan and visit the 9/11 Memorial pools and museum. Walk across Brooklyn Bridge as the afternoon light hits the skyline. Explore DUMBO's waterfront for iconic Manhattan views. Evening dinner in the West Village's candlelit restaurants.", schedule: ["Statue of Liberty and Ellis Island", "9/11 Memorial and museum", "Brooklyn Bridge walk", "DUMBO waterfront", "West Village dinner"], image: "https://images.unsplash.com/photo-1485871981521-5b1fd3805eee?w=600&h=400&fit=crop&q=80" },
    { title: "Central Park & the Upper Side", desc: "Morning stroll through Central Park — Bethesda Fountain, the Bow Bridge, Strawberry Fields. Visit the Metropolitan Museum of Art (you could spend days here; focus on the Temple of Dendur and the rooftop garden views). Afternoon window-shopping on Fifth Avenue. Evening on the Top of the Rock observation deck as the city transitions from golden hour to a sea of lights.", schedule: ["Central Park morning stroll", "Metropolitan Museum of Art", "Fifth Avenue shopping", "Top of the Rock sunset"], image: "https://images.unsplash.com/photo-1534430480872-3498386e7856?w=600&h=400&fit=crop&q=80" },
    { title: "Brooklyn & Culture", desc: "Cross to Brooklyn for brunch in Williamsburg. Explore the Brooklyn Flea market and street art scene. Afternoon at the Brooklyn Museum or a walk through Prospect Park. Return to Manhattan for a Broadway show — the quintessential New York experience.", schedule: ["Williamsburg brunch", "Brooklyn Flea market", "Brooklyn Museum or Prospect Park", "Broadway show"], image: "https://images.unsplash.com/photo-1518235506717-e1ed3306a89b?w=600&h=400&fit=crop&q=80" },
    { title: "Chelsea, SoHo & Departure", desc: "Morning walk on the High Line — the elevated park built on an old rail line with Hudson River views. Browse the galleries of Chelsea and the boutiques of SoHo. Final New York bagel from a legendary deli. Transfer to the airport.", schedule: ["High Line walk", "Chelsea galleries", "SoHo boutiques", "Final NYC bagel", "Transfer to airport"], image: "https://images.unsplash.com/photo-1522083165195-3424ed129620?w=600&h=400&fit=crop&q=80" },
  ],
  tokyo: [
    { title: "Arrival & Shinjuku Neon", desc: "Land at Narita or Haneda and transfer by express train — your first taste of Japanese rail precision. Check into your hotel and head to Shinjuku for an evening exploration of its labyrinthine streets, tiny yakitori alleys of Omoide Yokocho (Memory Lane), and neon-lit entertainment district. First ramen bowl at a local favourite.", schedule: ["Express train transfer", "Hotel check-in", "Shinjuku evening exploration", "Omoide Yokocho yakitori alleys", "First ramen bowl"], image: "https://images.unsplash.com/photo-1540959733332-eab4deabeeaf?w=600&h=400&fit=crop&q=80" },
    { title: "Traditional Tokyo", desc: "Morning at Senso-ji, Tokyo’s oldest temple in Asakusa. Walk Nakamise-dori shopping street for traditional snacks and crafts. Take a river cruise to the futuristic Odaiba area. Afternoon in Meiji Shrine’s peaceful forest, then explore Harajuku’s Takeshita Street for Tokyo’s youth fashion culture. Evening in Shibuya — watch the famous crossing from above, then dinner in an izakaya.", schedule: ["Senso-ji temple in Asakusa", "Nakamise-dori shopping", "River cruise to Odaiba", "Meiji Shrine forest", "Harajuku Takeshita Street", "Shibuya Crossing", "Izakaya dinner"], image: "https://images.unsplash.com/photo-1545569341-9eb8b30979d9?w=600&h=400&fit=crop&q=80" },
    { title: "Tsukiji, Teamlab & Ginza", desc: "Early start at Tsukiji Outer Market for the freshest sushi breakfast and tamagoyaki. Spend the morning at teamLab Borderless digital art museum — an immersive, other-worldly experience. Afternoon in the upscale Ginza district for high-end shopping and a visit to a traditional kabuki theatre. Evening omakase dinner at a sushi counter with the chef as your guide.", schedule: ["Tsukiji Outer Market sushi breakfast", "teamLab Borderless", "Ginza shopping", "Kabuki theatre", "Omakase sushi dinner"], image: "https://images.unsplash.com/photo-1503899036084-c55cdd92da26?w=600&h=400&fit=crop&q=80" },
    { title: "Day Trip to Mount Fuji & Hakone", desc: "Full-day excursion to the Hakone region. Ride the Hakone Ropeway with Mount Fuji views, cruise Lake Ashi on a pirate ship, and visit the volcanic hot springs of Owakudani. Relax in a traditional onsen (hot spring bath) with views of Fuji. Return to Tokyo in the evening via the bullet train.", schedule: ["Hakone Ropeway with Fuji views", "Lake Ashi cruise", "Owakudani volcanic hot springs", "Traditional onsen", "Bullet train return to Tokyo"], image: "https://images.unsplash.com/photo-1536098561742-ca998e48cbcc?w=600&h=400&fit=crop&q=80" },
    { title: "Akihabara & Pop Culture", desc: "Dive into Tokyo’s pop culture in Akihabara — multi-storey anime shops, maid cafés, and retro game arcades. Afternoon in Nakano Broadway for vintage collectibles. Visit a themed restaurant for a uniquely Tokyo dining experience. Evening in the tiny golden-lit bars of Shinjuku’s Golden Gai.", schedule: ["Akihabara anime and gaming district", "Maid cafés and retro arcades", "Nakano Broadway collectibles", "Themed restaurant", "Shinjuku Golden Gai bars"], image: "https://images.unsplash.com/photo-1493976040374-85c8e12f0c0e?w=600&h=400&fit=crop&q=80" },
    { title: "Yanaka & Craft Culture", desc: "Explore the old-town charm of Yanaka — one of Tokyo’s few neighbourhoods that survived wartime bombing. Visit artisan workshops for pottery, indigo dyeing, and calligraphy. Walk the atmospheric Yanaka Cemetery lined with cherry trees. Afternoon tea ceremony experience at a traditional tea house. Final evening in Roppongi’s restaurant scene.", schedule: ["Yanaka old-town exploration", "Artisan workshops", "Yanaka Cemetery", "Traditional tea ceremony", "Roppongi dinner"], image: "https://images.unsplash.com/photo-1540959733332-eab4deabeeaf?w=600&h=400&fit=crop&q=80" },
    { title: "Imperial Palace & Departure", desc: "Morning walk around the Imperial Palace East Gardens — the former site of Edo Castle with beautiful moats and stone walls. Last-minute souvenir shopping at Tokyo Station’s underground city. Grab ekiben (station lunch boxes) for the journey. Transfer to the airport.", schedule: ["Imperial Palace East Gardens", "Tokyo Station underground shopping", "Ekiben for the journey", "Transfer to airport"], image: "https://images.unsplash.com/photo-1545569341-9eb8b30979d9?w=600&h=400&fit=crop&q=80" },
  ],
  singapore: [
    { title: "Arrival & Marina Bay Spectacle", desc: "Arrive at Changi Airport — consistently voted the world’s best. Check into your hotel and head to Marina Bay Sands for sunset views from the SkyPark observation deck. Watch the Spectra light and water show from the waterfront promenade. Dinner at a celebrity chef restaurant along the bay.", schedule: ["Changi Airport arrival", "Hotel check-in", "Marina Bay Sands SkyPark sunset", "Spectra light show", "Celebrity chef dinner"], image: "https://images.unsplash.com/photo-1525625293386-3f8f99389edd?w=600&h=400&fit=crop&q=80" },
    { title: "Gardens, Culture & Hawkers", desc: "Morning at Gardens by the Bay — explore the Flower Dome and Cloud Forest conservatories. Walk the OCBC Skyway between the Supertrees. Afternoon heritage walk through Chinatown, Little India, and Kampong Glam (Arab Quarter). Evening at a Michelin-starred hawker stall in Maxwell Food Centre — Singapore’s famous street food elevated to art.", schedule: ["Gardens by the Bay", "Flower Dome and Cloud Forest", "OCBC Skyway", "Chinatown, Little India, Kampong Glam", "Maxwell Food Centre hawker dinner"], image: "https://images.unsplash.com/photo-1508964942454-1a56651d54ac?w=600&h=400&fit=crop&q=80" },
    { title: "Sentosa Island Adventure", desc: "Full day on Sentosa Island. Morning at Universal Studios Singapore for thrill rides. Afternoon on Palawan Beach or S.E.A. Aquarium — one of the world’s largest. Sunset cable car ride back to the mainland with sweeping harbour views. Evening in Clarke Quay’s riverside bars and restaurants.", schedule: ["Universal Studios Singapore", "Palawan Beach or S.E.A. Aquarium", "Sunset cable car ride", "Clarke Quay riverside evening"], image: "https://images.unsplash.com/photo-1565967511849-76a60a516170?w=600&h=400&fit=crop&q=80" },
    { title: "Neighbourhoods & Nature", desc: "Morning trek through the Southern Ridges trail — a 10km elevated walkway through jungle canopy connecting three parks. Afternoon exploring the Peranakan heritage of Katong and Joo Chiat, with their colourful shophouses and traditional kueh (cakes). Evening Night Safari at Singapore Zoo — the world’s first nocturnal wildlife park.", schedule: ["Southern Ridges jungle trail", "Katong and Joo Chiat Peranakan heritage", "Colourful shophouses", "Night Safari at Singapore Zoo"], image: "https://images.unsplash.com/photo-1496939376851-89342e90adcd?w=600&h=400&fit=crop&q=80" },
    { title: "Breakfast at Tiong Bahru & Departure", desc: "Morning stroll through Tiong Bahru, Singapore’s hippest heritage neighbourhood, for artisan coffee and egg tarts at a traditional kopitiam. Last-minute shopping at Orchard Road. Transfer to Changi Airport — arrive early to enjoy the Jewel’s Rain Vortex, the world’s tallest indoor waterfall.", schedule: ["Tiong Bahru neighbourhood stroll", "Artisan coffee at kopitiam", "Orchard Road shopping", "Jewel Rain Vortex at Changi"], image: "https://images.unsplash.com/photo-1564501049412-61c2a3083791?w=600&h=400&fit=crop&q=80" },
  ],
  amsterdam: [
    { title: "Arrival & Canal Belt", desc: "Arrive at Schiphol and transfer to your canal-house hotel in the Jordaan. Afternoon canal cruise through the UNESCO-listed ring canals. Watch the city’s golden-age architecture glide by from the water. Evening dinner at a traditional brown café followed by a jenever (Dutch gin) tasting.", schedule: ["Schiphol transfer to Jordaan hotel", "Afternoon canal cruise", "UNESCO ring canals", "Brown café dinner", "Jenever tasting"], image: "https://images.unsplash.com/photo-1534351590666-13e3e96b5017?w=600&h=400&fit=crop&q=80" },
    { title: "Masters & Museums", desc: "Morning at the Rijksmuseum for Rembrandt’s Night Watch and Vermeer’s Milkmaid. Cross to the Van Gogh Museum for the world’s largest collection of his works. Afternoon stroll through the Albert Cuyp market for stroopwafels and Dutch cheese. Evening visit to the Anne Frank House (pre-booked tickets essential).", schedule: ["Rijksmuseum", "Van Gogh Museum", "Albert Cuyp market", "Anne Frank House"], image: "https://images.unsplash.com/photo-1583037189850-1921ae7c6c22?w=600&h=400&fit=crop&q=80" },
    { title: "Jordaan & Local Life", desc: "Guided walking tour of the Jordaan — Amsterdam’s most characterful neighbourhood, with its hidden courtyards (hofjes), vintage shops, and artisan cheese makers. Afternoon cycling tour along the Amstel River to a traditional windmill. Return for Indonesian rijsttafel dinner — Amsterdam’s colonial culinary legacy.", schedule: ["Jordaan walking tour", "Hidden courtyards and hofjes", "Cycling along Amstel River", "Traditional windmill", "Indonesian rijsttafel dinner"], image: "https://images.unsplash.com/photo-1512470876302-972faa2aa9a4?w=600&h=400&fit=crop&q=80" },
    { title: "Zaanse Schans & Departure", desc: "Morning excursion to Zaanse Schans — a living museum of Dutch heritage with working windmills, a clog workshop, and a cheese farm. Last coffee and apple pie in a waterside café. Transfer to Schiphol Airport.", schedule: ["Zaanse Schans windmills", "Clog workshop", "Cheese farm", "Waterside café", "Transfer to Schiphol"], image: "https://images.unsplash.com/photo-1576924542622-772281b13aa8?w=600&h=400&fit=crop&q=80" },
  ],
  "hong kong": [
    { title: "Arrival in Hong Kong", desc: "The Airport Express whisks you from HK International to Central station in just 24 minutes — one of the fastest airport links in Asia. Check in and take the Star Ferry across Victoria Harbour at dusk for stunning skyline views, then feast on dim sum in Tsim Sha Tsui.", schedule: ["Airport transfer", "Hotel check-in", "Victoria Harbour evening", "Dim sum dinner"], image: "https://images.unsplash.com/photo-1536599018102-9f803c140fc1?w=600&h=400&fit=crop&q=80" },
    { title: "Hong Kong Island", desc: "Ride the 130-year-old Peak Tram up to Victoria Peak for panoramic views of the harbour and Kowloon skyline from the Sky Terrace 428. After exploring Central's art galleries, take the iconic Star Ferry to Kowloon for Temple Street Night Market and the nightly Symphony of Lights show at 8pm.", schedule: ["Breakfast at hotel", "Peak Tram to Victoria Peak", "Central district walk", "Star Ferry to Kowloon", "Temple Street Night Market", "Symphony of Lights show"], image: "https://images.unsplash.com/photo-1536599424071-0b215a388ba7?w=600&h=400&fit=crop&q=80" },
    { title: "Culture & Temples", desc: "Visit the vibrant Wong Tai Sin Temple where locals pray for good fortune, then find unexpected tranquility at Chi Lin Nunnery, a Tang Dynasty-style complex built entirely without nails. Browse the chaotic stalls of Mong Kok's Ladies' Market and Sneaker Street before heading up to a rooftop bar.", schedule: ["Breakfast at hotel", "Wong Tai Sin Temple", "Chi Lin Nunnery", "Nan Lian Garden", "Mong Kok markets", "Rooftop bar evening"], image: "https://images.unsplash.com/photo-1518599807935-37015b9cefcb?w=600&h=400&fit=crop&q=80" },
    { title: "Lantau Island", desc: "Take the Ngong Ping 360 crystal-floor cable car across Tung Chung Bay to the 34-metre-tall Tian Tan Big Buddha — climb its 268 steps for breathtaking mountain views. Continue to Tai O fishing village, built on stilts over the Pearl River Delta, for fresh seafood and a glimpse of traditional Hong Kong.", schedule: ["Breakfast at hotel", "Ngong Ping 360 cable car", "Tian Tan Buddha", "Po Lin Monastery", "Tai O fishing village", "Return and farewell dinner"], image: "https://images.unsplash.com/photo-1531259683007-016a7b628fc3?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Grab an egg tart and milk tea from a cha chaan teng for the road before heading to the airport. Hong Kong's airport has excellent duty-free shopping for electronics and luxury brands.", schedule: ["Final breakfast", "Last shopping", "Transfer to airport"], image: "https://images.unsplash.com/photo-1594973782943-3314fe063f68?w=600&h=400&fit=crop&q=80" },
  ],
  "cairo & luxor": [
    { title: "Arrival in Cairo", desc: "Transfer from Cairo International to your hotel near Tahrir Square — traffic can be intense so allow an hour. Take an evening walk along the Nile Corniche and enjoy a traditional Egyptian dinner of koshari and grilled kofta at a local restaurant.", schedule: ["Airport transfer", "Hotel check-in", "Nile evening walk", "Traditional Egyptian dinner"], image: "https://images.unsplash.com/photo-1572252009286-268acec5ca0a?w=600&h=400&fit=crop&q=80" },
    { title: "Pyramids & Sphinx", desc: "Arrive at the Giza Plateau by 8am before the heat and tour buses — the Great Pyramid of Khufu is the last standing Ancient Wonder of the World. After lunch at a terrace restaurant with pyramid views, explore the Grand Egyptian Museum and haggle for souvenirs in the lantern-lit alleyways of Khan el-Khalili bazaar.", schedule: ["Early breakfast", "Giza Pyramids complex", "Great Sphinx", "Lunch with pyramid views", "Egyptian Museum", "Khan el-Khalili bazaar evening"], image: "https://images.unsplash.com/photo-1503177119275-0aa32b3a9368?w=600&h=400&fit=crop&q=80" },
    { title: "Fly to Luxor", desc: "Catch the one-hour morning flight to Luxor and head straight to the West Bank's Valley of the Kings, where 63 pharaohs were entombed including Tutankhamun. Visit the columned Temple of Hatshepsut carved into the cliffs, then cross to Karnak Temple for a sunset walk among its 134 massive sandstone columns.", schedule: ["Early flight to Luxor", "Valley of the Kings", "Temple of Hatshepsut", "Colossi of Memnon", "Lunch", "Karnak Temple at sunset"], image: "https://images.unsplash.com/photo-1572252009286-268acec5ca0a?w=600&h=400&fit=crop&q=80" },
    { title: "Luxor Temples", desc: "Explore the beautifully lit Luxor Temple on the East Bank, then take a felucca sailboat along the Nile with Theban Hills as a backdrop — the two-hour sunset cruise is pure magic. Visit the Luxor Museum for exquisite New Kingdom artefacts before your return flight to Cairo.", schedule: ["Breakfast at hotel", "Luxor Temple", "Nile felucca ride", "Lunch by the Nile", "Luxor Museum", "Return flight to Cairo"], image: "https://images.unsplash.com/photo-1553913861-c0fddf2619ee?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Enjoy a final breakfast of foul medames and fresh pita, then pick up papyrus art or Egyptian cotton from the hotel gift shop before transferring to Cairo airport.", schedule: ["Final breakfast", "Last shopping", "Transfer to airport"], image: "https://images.unsplash.com/photo-1539650116574-8efeb43e2750?w=600&h=400&fit=crop&q=80" },
  ],
  phuket: [
    { title: "Arrival & Patong Sunset", desc: "Arrive at Phuket International and transfer to your resort. Settle in and head to the beachfront for your first Thai sunset — the Andaman Sea turns gold and pink. Dinner at a seaside restaurant sampling fresh prawns and pad thai.", schedule: ["Airport transfer to resort", "Beachfront Thai sunset", "Seaside dinner"], image: "https://images.unsplash.com/photo-1540541338287-41700207dee6?w=600&h=400&fit=crop&q=80" },
    { title: "Phang Nga Bay & James Bond Island", desc: "Full-day longtail boat excursion to Phang Nga Bay. Kayak through sea caves and hongs (collapsed cave lagoons), pass the iconic James Bond Island, and explore the floating Muslim village of Koh Panyee. Lunch on the water. Return via a quiet beach for a swim.", schedule: ["Longtail boat to Phang Nga Bay", "Sea cave kayaking", "James Bond Island", "Koh Panyee floating village", "Quiet beach swim"], image: "https://images.unsplash.com/photo-1519451241324-20b4ea2c4220?w=600&h=400&fit=crop&q=80" },
    { title: "Old Phuket Town & Culture", desc: "Morning exploring Old Phuket Town's charming Sino-Portuguese architecture — pastel shophouses, street art, and local cafés. Visit the Thai Hua Museum for the island's tin-mining and cultural history. Afternoon at the Big Buddha — Phuket's 45-metre hilltop statue with panoramic views. Evening at a night market for local food and handicrafts.", schedule: ["Old Phuket Town exploration", "Thai Hua Museum", "Big Buddha panoramic views", "Night market"], image: "https://images.unsplash.com/photo-1540541338287-41700207dee6?w=600&h=400&fit=crop&q=80" },
    { title: "Phi Phi Islands", desc: "Speedboat day trip to the Phi Phi Islands. Snorkel in the crystal waters of Pileh Lagoon, visit Maya Bay (made famous by The Beach), and swim at Bamboo Island. Seafood lunch on Phi Phi Don. Return to Phuket for a quiet evening.", schedule: ["Speedboat to Phi Phi Islands", "Pileh Lagoon snorkelling", "Maya Bay", "Bamboo Island swim", "Phi Phi Don seafood lunch"], image: "https://images.unsplash.com/photo-1507525428034-b723cf961d3e?w=600&h=400&fit=crop&q=80" },
    { title: "Beaches & Wellness", desc: "Morning at a quieter beach — Kata Noi or Freedom Beach, accessible by longtail boat. Afternoon Thai massage and spa treatment at a hillside wellness retreat. Sunset at Promthep Cape — the most famous viewpoint in Phuket.", schedule: ["Kata Noi or Freedom Beach", "Thai massage and spa", "Promthep Cape sunset"], image: "https://images.unsplash.com/photo-1537956965359-7573183d1f57?w=600&h=400&fit=crop&q=80" },
    { title: "Elephant Sanctuary & Cooking Class", desc: "Morning at an ethical elephant sanctuary — feed, bathe, and walk alongside these gentle giants in a natural forest setting. Afternoon Thai cooking class at a local home — learn to make green curry, tom kha gai, and mango sticky rice. Enjoy what you've cooked for dinner.", schedule: ["Ethical elephant sanctuary", "Thai cooking class", "Dinner from cooking class"], image: "https://images.unsplash.com/photo-1540541338287-41700207dee6?w=600&h=400&fit=crop&q=80" },
    { title: "Leisure & Departure", desc: "Final morning for beach time or last-minute shopping at the Chillva Night Market area. Transfer to the airport.", schedule: ["Final beach time or shopping", "Transfer to airport"], image: "https://images.unsplash.com/photo-1519451241324-20b4ea2c4220?w=600&h=400&fit=crop&q=80" },
  ],
  "bangkok & phuket": [
    { title: "Arrival in Bangkok", desc: "Arrive at Suvarnabhumi and take the Airport Rail Link to central Bangkok in 30 minutes. Drop your bags and dive into the backpacker buzz of Khao San Road for your first taste of Thai street food — pad thai and mango sticky rice from cart vendors cost under 100 baht.", schedule: ["Airport transfer", "Hotel check-in", "Khao San Road evening", "Street food dinner"], image: "https://images.unsplash.com/photo-1563492065599-3520f775eeed?w=600&h=400&fit=crop&q=80" },
    { title: "Bangkok Temples", desc: "See the Grand Palace's dazzling Emerald Buddha and the 46-metre reclining Buddha at Wat Pho — dress modestly with covered shoulders and knees. Cross the river to the stunning Wat Arun at sunset, then head to a rooftop sky bar for cocktails with the Bangkok skyline glittering below.", schedule: ["Early breakfast", "Grand Palace", "Wat Pho", "Wat Arun", "Chao Phraya cruise", "Rooftop bar sunset"], image: "https://images.unsplash.com/photo-1563492065599-3520f775eeed?w=600&h=400&fit=crop&q=80" },
    { title: "Fly to Phuket", desc: "Catch the 75-minute domestic flight from Bangkok to Phuket and swap the city buzz for white sand and warm Andaman waters. Check into your beachfront resort and spend the afternoon between pool and sea before a fresh grilled seafood dinner on the sand.", schedule: ["Morning flight to Phuket", "Resort check-in", "Beach afternoon", "Seafood dinner"], image: "https://images.unsplash.com/photo-1589394815804-964ed0be2eb5?w=600&h=400&fit=crop&q=80" },
    { title: "Phuket Islands", desc: "Board a speedboat for the stunning Phi Phi Islands — snorkel in the crystal-clear waters of Maya Bay and Pileh Lagoon among tropical fish and reef sharks. Return on a sunset cruise with the limestone karsts turning golden in the evening light.", schedule: ["Breakfast at resort", "Phi Phi Islands tour", "Snorkeling", "Beach lunch", "Return cruise", "Beach club evening"], image: "https://images.unsplash.com/photo-1519451241324-20b4ea2c4220?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Enjoy a final swim and a Thai iced tea before transferring to Phuket airport. Allow 60 minutes for the drive from the southern beaches.", schedule: ["Final breakfast", "Last beach time", "Transfer to airport"], image: "https://images.unsplash.com/photo-1537956965359-7573183d1f57?w=600&h=400&fit=crop&q=80" },
  ],
  venice: [
    { title: "Arrival in Venice", desc: "Take a private water taxi from Marco Polo airport across the lagoon to your hotel — the 30-minute boat ride past San Giorgio Maggiore is Venice's most dramatic entrance. Stroll to the Rialto Bridge at dusk when the Grand Canal glows golden and the gondoliers start their evening rounds.", schedule: ["Water taxi transfer", "Hotel check-in", "Evening canal walk", "Dinner near Rialto"], image: "https://images.unsplash.com/photo-1514890547357-a9ee288728e0?w=600&h=400&fit=crop&q=80" },
    { title: "St. Mark's & Grand Canal", desc: "Enter St. Mark's Basilica early morning (free entry, queues build fast) to see its 8,000 square metres of gold mosaics glittering in the candlelight. Book a skip-the-line ticket for the Doge's Palace and cross the Bridge of Sighs, then board a gondola for a 35-minute ride through the narrow back canals.", schedule: ["Breakfast at hotel", "St. Mark's Basilica", "Doge's Palace", "Campanile views", "Gondola ride", "Sunset at Grand Canal"], image: "https://images.unsplash.com/photo-1523906834658-6e24ef2386f9?w=600&h=400&fit=crop&q=80" },
    { title: "Islands & Art", desc: "Catch the vaporetto line 4.1 to Murano island to watch master glassblowers shape molten glass into intricate sculptures using techniques unchanged since the 13th century. Continue to Burano, where every house is painted a different bright colour — the island is famous for its handmade lace.", schedule: ["Breakfast at hotel", "Vaporetto to Murano", "Glass-making demonstration", "Ferry to Burano", "Colorful houses and lace", "Return to Venice", "Cicchetti dinner"], image: "https://images.unsplash.com/photo-1518623001395-125242310d0c?w=600&h=400&fit=crop&q=80" },
    { title: "Hidden Venice", desc: "Escape the crowds in arty Dorsoduro, home to the Accademia Gallery's Venetian masterpieces and the Peggy Guggenheim Collection. Cross to quiet Cannaregio for lunch, explore the historic Jewish Ghetto (the world's first, established 1516), and end with a spritz and cicchetti at a bacaro wine bar.", schedule: ["Breakfast at hotel", "Dorsoduro exploration", "Accademia Gallery", "Lunch in Cannaregio", "Jewish Ghetto history", "Aperitivo at sunset"], image: "https://images.unsplash.com/photo-1534113414509-0eec2bfb493f?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Savour a final Venetian breakfast of cornetto and cappuccino at a canal-side cafe before your water taxi to Marco Polo airport. Pack some Murano glass or Venetian carnival masks as keepsakes.", schedule: ["Final breakfast", "Last canal stroll", "Water taxi to airport"], image: "https://images.unsplash.com/photo-1498307833015-e7b400441eb8?w=600&h=400&fit=crop&q=80" },
  ],
  lisbon: [
    { title: "Arrival & Alfama at Dusk", desc: "Arrive at Lisbon Humberto Delgado Airport and transfer to your hotel. Evening walk through Alfama — Lisbon's oldest neighbourhood with its tangle of narrow streets, fado music drifting from doorways, and the Miradouro de Santa Luzia viewpoint as the city turns golden.", schedule: ["Airport transfer to hotel", "Alfama evening walk", "Miradouro de Santa Luzia"], image: "https://images.unsplash.com/photo-1585208798174-6cedd86e019a?w=600&h=400&fit=crop&q=80" },
    { title: "Belém & Age of Discovery", desc: "Morning tram ride to Belém — the district from which explorers set sail. Visit the ornate Jerónimos Monastery, the Tower of Belém on the riverbank, and the Monument to the Discoveries. Essential stop: Pastéis de Belém for the original custard tarts, warm from the oven with cinnamon. Afternoon at the MAAT contemporary art museum. Evening in Bairro Alto's lively restaurant streets.", schedule: ["Tram to Belém", "Jerónimos Monastery", "Tower of Belém", "Pastéis de Belém custard tarts", "MAAT museum", "Bairro Alto evening"], image: "https://images.unsplash.com/photo-1555881400-74d7acaacd8b?w=600&h=400&fit=crop&q=80" },
    { title: "Tram 28 & Hilltop Miradouros", desc: "Board the iconic Tram 28 and rattle through the steep streets of Graça and Alfama. Hop off at the Miradouro da Graça for sweeping views, then continue to the São Jorge Castle for panoramic city vistas. Afternoon exploring the azulejo tiles of the National Tile Museum. Evening fado performance in a traditional Alfama casa de fado — the soulful soundtrack of Lisbon.", schedule: ["Tram 28 ride", "Miradouro da Graça", "São Jorge Castle", "National Tile Museum", "Fado performance"], image: "https://images.unsplash.com/photo-1536663815808-535e2280d2c2?w=600&h=400&fit=crop&q=80" },
    { title: "Sintra Day Trip", desc: "Full-day excursion to Sintra — a UNESCO-listed fairytale town in forested hills. Explore the kaleidoscopic Pena Palace perched on a mountaintop, the mysterious Quinta da Regaleira with its initiation well, and the Moorish Castle ruins. Lunch at a Sintra restaurant. Optional stop at Cabo da Roca — the westernmost point of mainland Europe.", schedule: ["Sintra excursion", "Pena Palace", "Quinta da Regaleira", "Moorish Castle", "Cabo da Roca"], image: "https://images.unsplash.com/photo-1548707309-dcebeab9ea9b?w=600&h=400&fit=crop&q=80" },
    { title: "Time Out Market & Departure", desc: "Morning at the Time Out Market for a final Portuguese feast — pastéis de nata, bacalhau, and a vinho verde. Browse the vintage shops of Príncipe Real. Transfer to the airport.", schedule: ["Time Out Market", "Príncipe Real vintage shops", "Transfer to airport"], image: "https://images.unsplash.com/photo-1558618666-fcd25c85cd64?w=600&h=400&fit=crop&q=80" },
  ],
  prague: [
    { title: "Arrival & Old Town Square", desc: "Arrive at Václav Havel Airport and transfer to your hotel. Evening walk to Old Town Square — the Astronomical Clock, the twin-spired Týn Church, and the pastel baroque façades lit up at night. Dinner at a traditional Czech restaurant: svíčková (marinated sirloin) and a pilsner from the source.", schedule: ["Airport transfer to hotel", "Old Town Square evening walk", "Astronomical Clock", "Traditional Czech dinner"], image: "https://images.unsplash.com/photo-1519677100203-a0e668c92439?w=600&h=400&fit=crop&q=80" },
    { title: "Castle Quarter & Charles Bridge", desc: "Morning at Prague Castle — the world's largest ancient castle complex. Explore St Vitus Cathedral, the Golden Lane with its tiny alchemist houses, and the Old Royal Palace. Descend through the gardens to Malá Strana. Walk the Charles Bridge at midday (statues, musicians, and views of the Vltava). Evening at a jazz club in the Old Town.", schedule: ["Prague Castle complex", "St Vitus Cathedral", "Golden Lane", "Malá Strana", "Charles Bridge", "Jazz club evening"], image: "https://images.unsplash.com/photo-1541849546-216549ae216d?w=600&h=400&fit=crop&q=80" },
    { title: "Jewish Quarter & Vyšehrad", desc: "Morning in Josefov — the Jewish Quarter with its atmospheric synagogues, the Old Jewish Cemetery with 12,000 tombstones stacked in layers, and the Pinkas Synagogue memorial. Afternoon walk to Vyšehrad fortress for river views and the cemetery where Dvořák and Mucha are buried. Evening pub crawl through Prague's legendary beer halls.", schedule: ["Josefov Jewish Quarter", "Old Jewish Cemetery", "Pinkas Synagogue", "Vyšehrad fortress", "Prague beer hall pub crawl"], image: "https://images.unsplash.com/photo-1562624475-96c2bc08fab9?w=600&h=400&fit=crop&q=80" },
    { title: "Petřín Hill & Departure", desc: "Morning funicular to Petřín Hill — the Petřín Lookout Tower (Prague's mini Eiffel Tower) offers panoramic views. Walk through the rose gardens. Final coffee in a café in Malá Strana's charming streets. Transfer to the airport.", schedule: ["Petřín Hill funicular", "Petřín Lookout Tower", "Rose gardens", "Final Malá Strana coffee", "Transfer to airport"], image: "https://images.unsplash.com/photo-1458150945447-7fb764c11a92?w=600&h=400&fit=crop&q=80" },
  ],
  vienna: [
    { title: "Arrival in Vienna", desc: "Take the City Airport Train (CAT) from Vienna airport to Wien Mitte in just 16 minutes. Stroll the grand Ringstrasse boulevard past the Opera House, Parliament, and Rathaus, then enjoy a classic Wiener Schnitzel at a traditional Beisl restaurant.", schedule: ["Airport transfer", "Hotel check-in", "Ringstrasse evening walk", "Wiener Schnitzel dinner"], image: "https://images.unsplash.com/photo-1516550893923-42d28e5677af?w=600&h=400&fit=crop&q=80" },
    { title: "Imperial Vienna", desc: "Explore Schonbrunn Palace's 1,441 rooms and Baroque gardens — the Imperial Tour covers the most impressive state rooms in about 40 minutes. After lunch at the vibrant Naschmarkt food market, visit the Hofburg Imperial Palace and catch a performance at the Vienna State Opera (standing tickets are just a few euros).", schedule: ["Breakfast at hotel", "Schonbrunn Palace", "Palace gardens", "Lunch at Naschmarkt", "Hofburg Palace", "Spanish Riding School", "Vienna State Opera"], image: "https://images.unsplash.com/photo-1573599852326-2d4da0bbe613?w=600&h=400&fit=crop&q=80" },
    { title: "Art & Music", desc: "See Klimt's famous 'The Kiss' at the Belvedere Palace, then climb the 343 steps of St. Stephen's Cathedral south tower for a rooftop panorama of Vienna. Spend the afternoon in a traditional Viennese coffeehouse — Cafe Central or Demel — where the coffee ritual is UNESCO-listed.", schedule: ["Breakfast at hotel", "Belvedere Palace & Klimt", "St. Stephen's Cathedral", "Lunch in city center", "Kunsthistorisches Museum", "Traditional coffeehouse", "Concert evening"], image: "https://images.unsplash.com/photo-1548266652-99cf27701ced?w=600&h=400&fit=crop&q=80" },
    { title: "Prater & Wine", desc: "Ride the Riesenrad, Vienna's iconic 1897 Ferris wheel made famous in 'The Third Man', for views across the city from the Prater amusement park. In the evening, take the tram to Grinzing village for a Heuriger wine tavern experience with local Gruner Veltliner, cold meats, and live accordion music.", schedule: ["Breakfast at hotel", "Prater amusement park", "Giant Ferris Wheel", "Lunch in Prater", "Heuriger wine tavern", "Grinzing village", "Farewell dinner"], image: "https://images.unsplash.com/photo-1609856878074-cf31e21ccb6b?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Treat yourself to one last Sachertorte — the original is at Hotel Sacher — and pick up Mozart Kugeln chocolates as gifts before the CAT back to the airport.", schedule: ["Final breakfast", "Last Sachertorte", "Transfer to airport"], image: "https://images.unsplash.com/photo-1513805959324-96eb66ca8713?w=600&h=400&fit=crop&q=80" },
  ],
  milan: [
    { title: "Arrival in Milan", desc: "Take the Malpensa Express train to Milano Centrale in 50 minutes. Check in and head to the Navigli canal district for your first Milanese aperitivo — a Negroni or Aperol Spritz with generous free buffet snacks is the local evening ritual.", schedule: ["Airport transfer", "Hotel check-in", "Navigli canal evening", "Aperitivo and dinner"], image: "https://images.unsplash.com/photo-1513581166391-887a96ddeafd?w=600&h=400&fit=crop&q=80" },
    { title: "Duomo & Fashion", desc: "Climb to the Duomo rooftop (take the lift or 250 steps) to walk among the cathedral's 3,400 marble statues with the Alps visible on clear days. Browse the stunning Galleria Vittorio Emanuele II, Italy's oldest shopping mall, then explore the Quadrilatero della Moda fashion district — Via Montenapoleone is Milan's answer to Bond Street.", schedule: ["Breakfast at hotel", "Milan Cathedral", "Duomo rooftop", "Galleria Vittorio Emanuele II", "Lunch", "Quadrilatero della Moda", "La Scala area"], image: "https://images.unsplash.com/photo-1520440229-6469a149ac59?w=600&h=400&fit=crop&q=80" },
    { title: "Art & History", desc: "Book your Last Supper viewing months ahead — only 25 people enter the refectory at a time for a 15-minute viewing of Leonardo da Vinci's masterpiece. Explore the massive Sforza Castle and its Michelangelo sculptures, then stroll bohemian Brera with its art gallery, cobbled streets, and excellent trattorias.", schedule: ["Breakfast at hotel", "Last Supper viewing", "Sforza Castle", "Lunch in Brera", "Pinacoteca di Brera", "San Maurizio church", "Dinner in Brera"], image: "https://images.unsplash.com/photo-1577083165633-14ebcdb0f658?w=600&h=400&fit=crop&q=80" },
    { title: "Design & Lifestyle", desc: "Visit the striking Fondazione Prada, designed by Rem Koolhaas, where contemporary art meets a converted distillery. Explore the Zona Tortona design district, then head to a factory outlet like Serravalle (1 hour by bus) for discounted Italian fashion brands.", schedule: ["Breakfast at hotel", "Fondazione Prada", "Design District", "Lunch at trendy spot", "Shopping outlets", "Sunset aperitivo", "Farewell dinner"], image: "https://images.unsplash.com/photo-1555396273-367ea4eb4db5?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Have one last espresso at a standing bar (the proper Milanese way) before catching the Malpensa Express back to the airport. Grab panettone or amaretti biscuits as gifts.", schedule: ["Final breakfast", "Last espresso", "Transfer to airport"], image: "https://images.unsplash.com/photo-1577083165633-14ebcdb0f658?w=600&h=400&fit=crop&q=80" },
  ],
  madrid: [
    { title: "Arrival in Madrid", desc: "Take the metro from Barajas airport to Sol station in 30 minutes for just a few euros. Walk through the grand Plaza Mayor, ringed by Habsburg-era buildings, and settle into a table at one of the surrounding tapas bars for patatas bravas and jamon iberico.", schedule: ["Airport transfer", "Hotel check-in", "Plaza Mayor evening", "Tapas dinner"], image: "https://images.unsplash.com/photo-1543783207-ec64e4d95325?w=600&h=400&fit=crop&q=80" },
    { title: "Royal Madrid", desc: "Tour the Royal Palace of Madrid, the largest functioning royal palace in Europe with 3,418 rooms — the Throne Room and Royal Armoury are highlights. Graze through the gourmet stalls of Mercado San Miguel, then spend the afternoon at the Prado Museum, home to Velazquez's Las Meninas and Goya's Black Paintings.", schedule: ["Breakfast at hotel", "Royal Palace", "Almudena Cathedral", "Mercado San Miguel", "Prado Museum", "Retiro Park sunset", "Flamenco show"], image: "https://images.unsplash.com/photo-1573599852326-2d4da0bbe613?w=600&h=400&fit=crop&q=80" },
    { title: "Art & Culture", desc: "See Picasso's monumental Guernica at the Reina Sofia Museum (free after 7pm on weekdays), then complete Madrid's Art Triangle with the Thyssen-Bornemisza collection. Stroll the buzzing Gran Via boulevard and end the evening at a rooftop terrace bar with views of the illuminated Metropolis building.", schedule: ["Breakfast at hotel", "Reina Sofia Museum", "Guernica", "Lunch in La Latina", "Thyssen Museum", "Gran Via walk", "Rooftop bar evening"], image: "https://images.unsplash.com/photo-1558370781-d6196949e317?w=600&h=400&fit=crop&q=80" },
    { title: "Local Life", desc: "Sleep late like the madrilenos and head to El Rastro flea market (Sundays only, La Latina neighbourhood) for antiques and leather goods. Embrace the Spanish siesta culture, then explore trendy Malasana's vintage shops and craft cocktail bars as the city comes alive again after 9pm.", schedule: ["Late breakfast", "El Rastro market", "Tapas crawl", "Siesta time", "Malasana neighborhood", "Sunset drinks", "Farewell dinner"], image: "https://images.unsplash.com/photo-1570698473651-b2de99bae12f?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Grab a final churros con chocolate at Chocolateria San Gines (open since 1894, open 24 hours) before heading to the airport.", schedule: ["Final breakfast", "Last tapas", "Transfer to airport"], image: "https://images.unsplash.com/photo-1509840841025-9088ba78a826?w=600&h=400&fit=crop&q=80" },
  ],
  istanbul: [
    { title: "Arrival in Istanbul", desc: "Transfer from Istanbul airport to Sultanahmet via the Havaist bus (about 90 minutes). Walk the floodlit streets between Hagia Sophia and the Blue Mosque at night — the historical peninsula is magical after dark — and enjoy a spread of meze and grilled kebabs at a local restaurant.", schedule: ["Airport transfer", "Hotel check-in", "Sultanahmet evening walk", "Turkish dinner"], image: "https://images.unsplash.com/photo-1524231757912-21f4fe3a7200?w=600&h=400&fit=crop&q=80" },
    { title: "Sultanahmet Treasures", desc: "Enter Hagia Sophia, a building that has served as both cathedral and mosque for 1,500 years — its 55-metre dome remains one of the largest in the world. Cross to the Blue Mosque (free, remove shoes, women cover heads), then descend into the atmospheric Basilica Cistern with its 336 marble columns and Medusa heads.", schedule: ["Breakfast at hotel", "Hagia Sophia", "Blue Mosque", "Lunch at Sultanahmet", "Basilica Cistern", "Hippodrome", "Turkish bath experience"], image: "https://images.unsplash.com/photo-1541432901042-2d8bd64b4a9b?w=600&h=400&fit=crop&q=80" },
    { title: "Topkapi & Grand Bazaar", desc: "Explore Topkapi Palace where Ottoman sultans ruled for 400 years — the Harem section and Treasury room with the 86-carat Spoonmaker's Diamond are highlights. Lose yourself in the Grand Bazaar's 4,000 shops spread across 61 covered streets, haggling for ceramics, lanterns, and Turkish carpets.", schedule: ["Breakfast at hotel", "Topkapi Palace", "Harem tour", "Lunch near palace", "Grand Bazaar shopping", "Spice Bazaar", "Dinner on the Bosphorus"], image: "https://images.unsplash.com/photo-1558642452-9d2a7deb7f62?w=600&h=400&fit=crop&q=80" },
    { title: "Bosphorus & Asian Side", desc: "Board a public ferry for a 90-minute Bosphorus cruise between Europe and Asia, passing Ottoman waterfront mansions and the Rumeli Fortress. Hop off at Kadikoy on the Asian side for the vibrant food market, then return for a stroll up Istiklal Street, Istanbul's bustling 1.4-km pedestrian avenue.", schedule: ["Breakfast at hotel", "Bosphorus cruise", "Asian side exploration", "Kadikoy market lunch", "Dolmabahce Palace", "Istiklal Street", "Farewell meze dinner"], image: "https://images.unsplash.com/photo-1527838832700-5059252407fa?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Enjoy a traditional Turkish breakfast spread (simit, olives, cheese, honey, eggs) before heading to the airport. Pick up Turkish delight, baklava, or hand-painted ceramics as gifts.", schedule: ["Final breakfast", "Last bazaar visit", "Transfer to airport"], image: "https://images.unsplash.com/photo-1541432901042-2d8bd64b4a9b?w=600&h=400&fit=crop&q=80" },
  ],
  berlin: [
    { title: "Arrival in Berlin", desc: "Take the FEX express train from BER airport to Hauptbahnhof in 30 minutes. Walk the grand Unter den Linden boulevard from Brandenburg Gate to Museum Island and enjoy a hearty German dinner of Eisbein or currywurst at a traditional Kneipe pub.", schedule: ["Airport transfer", "Hotel check-in", "Unter den Linden evening walk", "German dinner"], image: "https://images.unsplash.com/photo-1560969184-10fe8719e047?w=600&h=400&fit=crop&q=80" },
    { title: "Historic Berlin", desc: "Walk through Brandenburg Gate, the symbol of German reunification, then visit the Reichstag's stunning glass dome (book free tickets online). The Holocaust Memorial's 2,711 concrete blocks are deeply moving, and the East Side Gallery's 1.3km of Berlin Wall murals — including the famous Bruderkuss — is unmissable.", schedule: ["Breakfast at hotel", "Brandenburg Gate", "Reichstag Building", "Holocaust Memorial", "Checkpoint Charlie", "Berlin Wall Memorial", "East Side Gallery"], image: "https://images.unsplash.com/photo-1587330979470-3595ac045ab0?w=600&h=400&fit=crop&q=80" },
    { title: "Museum Island", desc: "Spend the morning at the Pergamon Museum to see the reconstructed Ishtar Gate of Babylon and the enormous Pergamon Altar. Visit the Neues Museum for the famous bust of Nefertiti, then climb the dome of the Berlin Cathedral for views over Museum Island and the Spree River.", schedule: ["Breakfast at hotel", "Pergamon Museum", "Neues Museum", "Lunch on Museum Island", "Berlin Cathedral", "Alexanderplatz", "TV Tower views"], image: "https://images.unsplash.com/photo-1560969184-10fe8719e047?w=600&h=400&fit=crop&q=80" },
    { title: "Neighborhoods & Culture", desc: "Explore multicultural Kreuzberg's Turkish markets and street art, then walk to Prenzlauer Berg for third-wave coffee shops and boutiques along Kastanienallee. End the day at Tempelhof, Berlin's decommissioned airport turned into a massive park where locals barbecue, kitesurf, and urban garden on the old runways.", schedule: ["Breakfast at hotel", "Kreuzberg exploration", "Street food lunch", "Prenzlauer Berg cafes", "Mauerpark", "Sunset at Tempelhof", "Nightlife experience"], image: "https://images.unsplash.com/photo-1546726747-421c6d69c929?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Grab a final Berliner doughnut and coffee before heading to BER airport. Pick up Ritter Sport chocolate or Ampelmann souvenirs as Berlin keepsakes.", schedule: ["Final breakfast", "Last walk", "Transfer to airport"], image: "https://images.unsplash.com/photo-1599946347371-68eb71b16afc?w=600&h=400&fit=crop&q=80" },
  ],
  athens: [
    { title: "Arrival & Plaka Evening", desc: "Arrive at Athens International and transfer to your hotel in the historic centre. Evening stroll through Plaka — the oldest neighbourhood in Athens, nestled at the foot of the Acropolis. Dinner at a traditional taverna under olive trees with the Parthenon illuminated above.", schedule: ["Airport transfer to hotel", "Plaka evening stroll", "Traditional taverna dinner"], image: "https://images.unsplash.com/photo-1555993539-1732b0258235?w=600&h=400&fit=crop&q=80" },
    { title: "The Acropolis & Ancient Agora", desc: "Early morning visit to the Acropolis — the Parthenon, the Erechtheion with its Caryatid porch, and the sweeping views over the city. Descend to the Acropolis Museum to see the original sculptures up close. Afternoon in the Ancient Agora and the Temple of Hephaestus — the best-preserved Greek temple in existence. Evening in the Psyrri neighbourhood for meze and ouzo.", schedule: ["Acropolis early morning visit", "Parthenon and Erechtheion", "Acropolis Museum", "Ancient Agora", "Temple of Hephaestus", "Psyrri meze and ouzo"], image: "https://images.unsplash.com/photo-1603565816030-6b389eeb23cb?w=600&h=400&fit=crop&q=80" },
    { title: "Cape Sounion & Seaside", desc: "Half-day excursion to Cape Sounion — the Temple of Poseidon perched on a cliff 60 metres above the Aegean, one of the most dramatic settings in Greece. Watch the sunset that Lord Byron carved his name to witness. Return to Athens for dinner at a rooftop restaurant with Acropolis views.", schedule: ["Cape Sounion excursion", "Temple of Poseidon", "Byron's sunset", "Rooftop Acropolis dinner"], image: "https://images.unsplash.com/photo-1571406252241-db0280bd36cd?w=600&h=400&fit=crop&q=80" },
    { title: "Monastiraki & Departure", desc: "Morning exploring Monastiraki flea market for antiques, vinyl records, and leather sandals. Walk through the Anafiotika quarter — a whitewashed island-village hidden on the Acropolis slopes. Final Greek coffee and bougatsa (custard pastry). Transfer to the airport.", schedule: ["Monastiraki flea market", "Anafiotika quarter", "Final Greek coffee", "Transfer to airport"], image: "https://images.unsplash.com/photo-1533104816931-20fa691ff6ca?w=600&h=400&fit=crop&q=80" },
  ],
  miami: [
    { title: "Arrival in Miami", desc: "Transfer from MIA airport to South Beach in about 30 minutes. Walk the neon-lit Art Deco strip of Ocean Drive, then head to Calle Ocho in Little Havana for authentic Cuban food — the ventanitas (walk-up coffee windows) serve the best cortaditos in town.", schedule: ["Airport transfer", "Hotel check-in", "Ocean Drive evening", "Cuban dinner in Little Havana"], image: "https://images.unsplash.com/photo-1533106497176-45ae19e68ba2?w=600&h=400&fit=crop&q=80" },
    { title: "South Beach", desc: "Start with a morning swim in the warm turquoise waters of South Beach, then join a guided Art Deco walking tour of the pastel-coloured 1930s hotels along Ocean Drive and Collins Avenue. Lunch al fresco on Lincoln Road's pedestrian mall, where people-watching is half the fun.", schedule: ["Breakfast at hotel", "South Beach morning", "Art Deco walking tour", "Lunch on Lincoln Road", "Beach afternoon", "Sunset at South Pointe", "Club evening"], image: "https://images.unsplash.com/photo-1535498730771-e735b998cd64?w=600&h=400&fit=crop&q=80" },
    { title: "Art & Culture", desc: "Explore the Wynwood Walls, a 80,000-square-foot outdoor museum of street art murals by world-renowned artists — the surrounding warehouses now house galleries, breweries, and cafes. Cross to the Design District for high-end fashion and architecture, then visit the Perez Art Museum overlooking Biscayne Bay.", schedule: ["Breakfast at hotel", "Wynwood Walls", "Art galleries", "Lunch in Wynwood", "Design District", "Perez Art Museum", "Brickell evening"], image: "https://images.unsplash.com/photo-1571115177098-24ec42ed204d?w=600&h=400&fit=crop&q=80" },
    { title: "Everglades & Keys", desc: "Board an airboat for a thrilling ride through the Everglades, a 1.5-million-acre subtropical wilderness where you can spot alligators, manatees, and rare bird species. In the afternoon, drive across the causeway to Key Biscayne for its pristine beach and Crandon Park before a sunset cruise on Biscayne Bay.", schedule: ["Early breakfast", "Everglades airboat tour", "Alligator spotting", "Lunch", "Key Biscayne beach", "Sunset cruise", "Farewell dinner"], image: "https://images.unsplash.com/photo-1549989476-69a92fa57c36?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Enjoy a final cafecito and pastelito from a Cuban bakery before heading to the airport. Miami's duty-free has great cigar and rum selections.", schedule: ["Final breakfast", "Last beach time", "Transfer to airport"], image: "https://images.unsplash.com/photo-1506966953602-c20cc11f75e3?w=600&h=400&fit=crop&q=80" },
  ],
  "los angeles": [
    { title: "Arrival in LA", desc: "Transfer from LAX to your hotel — a car is essential in LA, so consider renting one on arrival. Head to Santa Monica Pier for your first Pacific sunset, ride the solar-powered Ferris wheel, and dine at a seafood restaurant overlooking the ocean.", schedule: ["Airport transfer", "Hotel check-in", "Santa Monica Pier sunset", "Dinner on the beach"], image: "https://images.unsplash.com/photo-1534190760961-74e8c1c5c3da?w=600&h=400&fit=crop&q=80" },
    { title: "Hollywood & Stars", desc: "Walk the Hollywood Walk of Fame's 2,700+ stars along Hollywood Boulevard and match your hands to celebrity prints at the TCL Chinese Theatre. Hike to the Griffith Observatory (free entry) for the best views of the Hollywood Sign and the LA basin stretching to the Pacific.", schedule: ["Breakfast at hotel", "Hollywood Walk of Fame", "TCL Chinese Theatre", "Hollywood Sign views", "Griffith Observatory", "Sunset Boulevard", "Beverly Hills dinner"], image: "https://images.unsplash.com/photo-1542204165-65bf26472b9b?w=600&h=400&fit=crop&q=80" },
    { title: "Beach Cities", desc: "Cruise the Venice Beach boardwalk past street performers, Muscle Beach bodybuilders, and the famous skate park, then browse the boutiques and cafes along Abbot Kinney Boulevard. Drive the Pacific Coast Highway north to Malibu for dinner at a cliffside restaurant with ocean views.", schedule: ["Breakfast at hotel", "Venice Beach boardwalk", "Muscle Beach", "Lunch in Venice", "Abbot Kinney shopping", "Malibu drive", "Sunset dinner in Malibu"], image: "https://images.unsplash.com/photo-1506146332389-18140dc7b2fb?w=600&h=400&fit=crop&q=80" },
    { title: "Arts & Culture", desc: "Visit the Getty Center (free admission, parking $20), perched in the Brentwood hills with stunning architecture by Richard Meier and views across LA to the ocean. Drive down to Rodeo Drive in Beverly Hills for window shopping, then explore LACMA's iconic Urban Light installation and the Downtown Arts District.", schedule: ["Breakfast at hotel", "Getty Center", "Rodeo Drive", "Lunch in Beverly Hills", "LACMA or Broad Museum", "Downtown Arts District", "Rooftop bar evening"], image: "https://images.unsplash.com/photo-1580655653885-65763b2597d0?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Have a final acai bowl or avocado toast (when in LA...) before heading to LAX. Allow plenty of time as LA traffic is notoriously unpredictable.", schedule: ["Final breakfast", "Last beach walk", "Transfer to LAX"], image: "https://images.unsplash.com/photo-1515896769750-31548aa180ed?w=600&h=400&fit=crop&q=80" },
  ],
  cancun: [
    { title: "Arrival & Caribbean First Dip", desc: "Arrive at Cancún International and transfer to your resort on the Hotel Zone strip. Spend the afternoon on that first plunge into the impossibly turquoise Caribbean. Sunset cocktails at a beachfront palapa bar.", schedule: ["Airport transfer to Hotel Zone", "First Caribbean swim", "Sunset palapa bar cocktails"], image: "https://images.unsplash.com/photo-1552074284-5e88ef1aef18?w=600&h=400&fit=crop&q=80" },
    { title: "Ancient Wonders — Chichén Itzá", desc: "Full-day excursion to Chichén Itzá, one of the New Seven Wonders of the World. Arrive early to explore El Castillo pyramid, the Ball Court, and the Temple of Warriors before the heat and crowds peak. Stop at a cenote for a refreshing swim on the return journey. Evening at leisure.", schedule: ["Chichén Itzá excursion", "El Castillo pyramid", "Ball Court and Temple of Warriors", "Cenote swim", "Evening at leisure"], image: "https://images.unsplash.com/photo-1518638150340-f706e86654de?w=600&h=400&fit=crop&q=80" },
    { title: "Isla Mujeres Island Escape", desc: "Ferry to Isla Mujeres — a laid-back Caribbean island a 20-minute ride from Cancún. Rent a golf cart and explore Punta Sur's clifftop sculpture garden, snorkel the underwater museum (MUSA), and enjoy fresh ceviche at a beachfront restaurant. Return at sunset.", schedule: ["Ferry to Isla Mujeres", "Punta Sur sculpture garden", "MUSA underwater museum snorkel", "Beachfront ceviche", "Sunset return"], image: "https://images.unsplash.com/photo-1510097467424-192d713fd8b2?w=600&h=400&fit=crop&q=80" },
    { title: "Cenotes & Underground Rivers", desc: "Morning exploring the Ruta de los Cenotes — swim in crystal-clear sinkholes formed in the limestone jungle. Visit at least two cenotes: an open-air one with jungle canopy above and a cavern cenote with light shafts piercing through. Afternoon at the resort pool. Evening Mexican cooking class.", schedule: ["Ruta de los Cenotes", "Open-air cenote swim", "Cavern cenote", "Resort pool afternoon", "Mexican cooking class"], image: "https://images.unsplash.com/photo-1502781252888-9143f9e3aa6c?w=600&h=400&fit=crop&q=80" },
    { title: "Tulum Ruins & Beach", desc: "Day trip to Tulum — the only Mayan ruins overlooking the Caribbean Sea. Explore the clifftop temples, then descend to the stunning beach below for a swim. Afternoon at a Tulum beach club with your toes in the sand. Return to Cancún for dinner.", schedule: ["Tulum Mayan ruins", "Clifftop temples", "Tulum beach swim", "Tulum beach club", "Return to Cancún"], image: "https://images.unsplash.com/photo-1510097467424-192d713fd8b2?w=600&h=400&fit=crop&q=80" },
    { title: "Snorkelling & Water Adventures", desc: "Morning snorkelling trip to the Mesoamerican Reef — the second-largest barrier reef in the world. Swim with sea turtles at Akumal Bay. Afternoon options: parasailing, jet-skiing, or simply relaxing at the resort. Farewell dinner at a fine-dining restaurant in the Hotel Zone.", schedule: ["Mesoamerican Reef snorkelling", "Akumal Bay sea turtles", "Water sports or resort relaxation", "Farewell dinner"], image: "https://images.unsplash.com/photo-1552074284-5e88ef1aef18?w=600&h=400&fit=crop&q=80" },
    { title: "Beach Morning & Departure", desc: "Final morning on the Caribbean sand. Depending on flight time, visit Mercado 28 in downtown Cancún for souvenirs — handcrafted silver, vanilla, and Mexican textiles. Transfer to the airport.", schedule: ["Final beach morning", "Mercado 28 shopping", "Transfer to airport"], image: "https://images.unsplash.com/photo-1518638150340-f706e86654de?w=600&h=400&fit=crop&q=80" },
  ],
  marrakech: [
    { title: "Arrival & Djemaa el-Fna", desc: "Arrive at Marrakech Menara Airport and transfer to your riad in the medina. As evening falls, plunge into the sensory overload of Djemaa el-Fna square — snake charmers, henna artists, storytellers, and dozens of food stalls sending fragrant smoke into the air. Dinner from the stalls: lamb tagine, grilled meats, and fresh orange juice.", schedule: ["Airport transfer to riad", "Djemaa el-Fna square", "Street food dinner"], image: "https://images.unsplash.com/photo-1597212618440-806262de4f6b?w=600&h=400&fit=crop&q=80" },
    { title: "Medina & Souks", desc: "Morning guided walking tour through the labyrinthine souks — the dyers' souk with skeins of colour dripping overhead, the metalworkers hammering copper, the leather tanners. Visit the Ben Youssef Madrasa with its exquisite zellige tilework. Afternoon at the Bahia Palace and the Saadian Tombs. Evening relaxation in your riad's courtyard with mint tea.", schedule: ["Guided souk walking tour", "Ben Youssef Madrasa", "Bahia Palace", "Saadian Tombs", "Riad mint tea"], image: "https://images.unsplash.com/photo-1539020140153-e479b8c22e70?w=600&h=400&fit=crop&q=80" },
    { title: "Jardin Majorelle & Nouvelle Ville", desc: "Morning at Jardin Majorelle — the cobalt-blue garden created by Jacques Majorelle and restored by Yves Saint Laurent. Visit the adjacent YSL Museum. Afternoon exploring the Gueliz neighbourhood — Marrakech's modern side with art galleries, concept stores, and European-style cafés. Sunset cocktails on a rooftop terrace overlooking the medina.", schedule: ["Jardin Majorelle", "YSL Museum", "Gueliz neighbourhood", "Rooftop sunset cocktails"], image: "https://images.unsplash.com/photo-1558791359-f79f2b1481a2?w=600&h=400&fit=crop&q=80" },
    { title: "Atlas Mountains Excursion", desc: "Full-day trip to the Atlas Mountains. Drive through Berber villages to the Ourika Valley. Hike to a mountain waterfall, visit a traditional Berber home for tea and amlou (almond butter), and enjoy a rustic lunch with valley views. Return to Marrakech for a hammam spa experience — steam, black soap scrub, and argan oil massage.", schedule: ["Atlas Mountains drive", "Ourika Valley hike", "Berber home visit", "Mountain waterfall", "Hammam spa experience"], image: "https://images.unsplash.com/photo-1493246507139-91e8fad9978e?w=600&h=400&fit=crop&q=80" },
    { title: "Palmeraie & Departure", desc: "Morning camel ride through the Palmeraie — Marrakech's palm grove oasis. Last shopping in the souks for leather goods, ceramics, and lanterns. Final mint tea. Transfer to the airport.", schedule: ["Palmeraie camel ride", "Souk shopping", "Final mint tea", "Transfer to airport"], image: "https://images.unsplash.com/photo-1539020140153-e479b8c22e70?w=600&h=400&fit=crop&q=80" },
  ],
  "cape town": [
    { title: "Arrival & V&A Waterfront", desc: "Arrive at Cape Town International and transfer to your hotel. Evening at the Victoria & Alfred Waterfront — Table Mountain illuminated as your backdrop, harbour-side restaurants, and the Zeitz Museum of Contemporary Art Africa glowing in the converted grain silo.", schedule: ["Airport transfer to hotel", "V&A Waterfront evening", "Zeitz Museum exterior", "Harbour-side dinner"], image: "https://images.unsplash.com/photo-1580060839134-75a5edca2e99?w=600&h=400&fit=crop&q=80" },
    { title: "Table Mountain & Bo-Kaap", desc: "Morning cable car up Table Mountain — the views over the city, Robben Island, and the Atlantic are extraordinary. Hike the plateau trails before descending. Afternoon walking tour of the Bo-Kaap neighbourhood with its brightly painted houses and Cape Malay heritage. Cooking class making Cape Malay curry and bobotie.", schedule: ["Table Mountain cable car", "Plateau trails hike", "Bo-Kaap walking tour", "Cape Malay cooking class"], image: "https://images.unsplash.com/photo-1516026672322-bc52d61a55d5?w=600&h=400&fit=crop&q=80" },
    { title: "Cape Peninsula Drive", desc: "Full-day scenic drive along the Cape Peninsula. Visit the penguin colony at Boulders Beach, drive through the fishing village of Kalk Bay, and continue to Cape Point where the Atlantic meets the Indian Ocean. Walk to the old lighthouse for views of the dramatic cliffs. Return via Chapman's Peak Drive — one of the world's most spectacular coastal roads.", schedule: ["Boulders Beach penguins", "Kalk Bay fishing village", "Cape Point lighthouse", "Chapman's Peak Drive"], image: "https://images.unsplash.com/photo-1576485375217-d6a95e34d043?w=600&h=400&fit=crop&q=80" },
    { title: "Winelands", desc: "Day trip to the Cape Winelands. Tour three estates in Stellenbosch and Franschhoek — South Africa's equivalent of Napa Valley but with Cape Dutch architecture and mountain backdrops. Gourmet lunch at a vineyard restaurant. Optional cheese and chocolate pairings. Return to Cape Town in the late afternoon.", schedule: ["Stellenbosch wine estate", "Franschhoek wine estate", "Vineyard restaurant lunch", "Cheese and chocolate pairings"], image: "https://images.unsplash.com/photo-1504279577054-acfeccf8fc52?w=600&h=400&fit=crop&q=80" },
    { title: "Robben Island & City", desc: "Morning ferry to Robben Island for a guided tour of the prison where Nelson Mandela spent 18 years — led by a former political prisoner. Return to the city for lunch in the Woodstock neighbourhood's craft breweries and food markets. Afternoon at the District Six Museum. Sunset from Signal Hill.", schedule: ["Robben Island ferry and tour", "Woodstock lunch", "District Six Museum", "Signal Hill sunset"], image: "https://images.unsplash.com/photo-1578874691223-64558a3ca096?w=600&h=400&fit=crop&q=80" },
    { title: "Camps Bay & Clifton", desc: "Relaxation day at the Atlantic Seaboard beaches — Camps Bay's palm-lined promenade or the sheltered coves of Clifton. Afternoon hike up Lion's Head for 360-degree views (or take it easy at a beach café). Farewell dinner at a rooftop restaurant overlooking the ocean.", schedule: ["Camps Bay or Clifton beach", "Lion's Head hike", "Farewell rooftop dinner"], image: "https://images.unsplash.com/photo-1580060839134-75a5edca2e99?w=600&h=400&fit=crop&q=80" },
    { title: "Kirstenbosch & Departure", desc: "Morning walk through Kirstenbosch National Botanical Garden — the Tree Canopy Walkway offers stunning perspectives. Last coffee at a Kloof Street café. Transfer to the airport.", schedule: ["Kirstenbosch Botanical Garden", "Tree Canopy Walkway", "Kloof Street café", "Transfer to airport"], image: "https://images.unsplash.com/photo-1516026672322-bc52d61a55d5?w=600&h=400&fit=crop&q=80" },
  ],
  sydney: [
    { title: "Arrival in Sydney", desc: "Take the Airport Link train from Sydney airport to Circular Quay in 25 minutes and step out to one of the world's great views — the Opera House and Harbour Bridge lit up against the evening sky. Walk along the foreshore to The Rocks for your first flat white and a harbour-side dinner.", schedule: ["Airport transfer", "Hotel check-in", "Circular Quay evening", "Dinner with Opera House views"], image: "https://images.unsplash.com/photo-1506973035872-a4ec16b8e8d9?w=600&h=400&fit=crop&q=80" },
    { title: "Harbour Icons", desc: "Join a backstage tour of the Sydney Opera House to see the concert halls and learn about Jorn Utzon's revolutionary design, then explore the cobbled laneways of The Rocks, Sydney's oldest neighbourhood. Book the BridgeClimb for a thrilling 3.5-hour ascent to the summit of the Harbour Bridge, 134 metres above the water, then catch the ferry to Manly Beach for sunset.", schedule: ["Breakfast at hotel", "Sydney Opera House tour", "The Rocks walking tour", "Lunch at Circular Quay", "Harbour Bridge climb or walk", "Ferry to Manly Beach", "Sunset return"], image: "https://images.unsplash.com/photo-1523428096881-5bd79d043006?w=600&h=400&fit=crop&q=80" },
    { title: "Beaches & Nature", desc: "Start with a morning swim at Bondi Beach, then walk the spectacular 6km Bondi to Coogee coastal trail past dramatic cliff faces, ocean pools, and Bronte's surf breaks. In the afternoon, take the ferry across the harbour to Taronga Zoo for close encounters with koalas and platypuses against a backdrop of the city skyline.", schedule: ["Breakfast at hotel", "Bondi Beach morning", "Bondi to Coogee walk", "Lunch at Bronte", "Taronga Zoo or aquarium", "Darling Harbour evening", "Dinner in Surry Hills"], image: "https://images.unsplash.com/photo-1524293581917-878a6d017c71?w=600&h=400&fit=crop&q=80" },
    { title: "Blue Mountains", desc: "Drive 90 minutes west to the Blue Mountains, a UNESCO World Heritage wilderness of eucalyptus forests and plunging valleys. Ride the world's steepest railway at Scenic World down into the ancient rainforest, then hike to Echo Point for views of the Three Sisters rock formation — the blue haze from the eucalyptus oil gives the mountains their name.", schedule: ["Early breakfast", "Blue Mountains day trip", "Three Sisters viewpoint", "Scenic Railway", "Bush walks", "Lunch in Leura", "Return to Sydney", "Farewell dinner"], image: "https://images.unsplash.com/photo-1505765050516-f72dcac9c60e?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Grab a final smashed avo and coffee at a Surry Hills cafe before heading to the airport. The Airport Link runs every 10 minutes from Central Station.", schedule: ["Final breakfast", "Last harbour views", "Transfer to airport"], image: "https://images.unsplash.com/photo-1524293581917-878a6d017c71?w=600&h=400&fit=crop&q=80" },
  ],
  melbourne: [
    { title: "Arrival & Southbank", desc: "Arrive at Melbourne Tullamarine Airport and transfer to the city. Settle into your hotel and explore Southbank’s riverside promenade. Dinner at a waterfront restaurant with city skyline views.", schedule: ["Airport transfer to city", "Southbank riverside promenade", "Waterfront dinner with skyline views"], image: "https://images.unsplash.com/photo-1514395462725-fb4566210144?w=600&h=400&fit=crop&q=80" },
    { title: "Laneways & Street Art", desc: "Guided walking tour of Melbourne’s famous laneways — Hosier Lane, AC/DC Lane, and Degraves Street. Coffee at one of the city’s award-winning roasteries. Afternoon at the National Gallery of Victoria (NGV) and Federation Square. Evening at a rooftop bar in the CBD.", schedule: ["Hosier Lane street art", "AC/DC Lane and Degraves Street", "Award-winning coffee", "NGV and Federation Square", "Rooftop bar evening"], image: "https://images.unsplash.com/photo-1545044846-351ba102b6d5?w=600&h=400&fit=crop&q=80" },
    { title: "Great Ocean Road", desc: "Full-day road trip along one of the world’s most scenic coastal drives. Stop at Bells Beach, the rainforest walk at Great Otway National Park, and the iconic Twelve Apostles limestone formations. Watch the sunset over the Southern Ocean before returning to Melbourne.", schedule: ["Bells Beach", "Great Otway rainforest walk", "Twelve Apostles", "Southern Ocean sunset", "Return to Melbourne"], image: "https://images.unsplash.com/photo-1529108190281-9a4f620bc2d8?w=600&h=400&fit=crop&q=80" },
    { title: "Food & Markets", desc: "Morning browsing Queen Victoria Market — Melbourne’s beloved 140-year-old market. Join a food tour through the multicultural suburbs of Richmond (Vietnamese) and Lygon Street (Italian). Afternoon at the Melbourne Cricket Ground (MCG) for a behind-the-scenes tour. Evening dinner in Chinatown.", schedule: ["Queen Victoria Market", "Richmond food tour", "Lygon Street Italian quarter", "MCG behind-the-scenes tour", "Chinatown dinner"], image: "https://images.unsplash.com/photo-1508317469940-e3de49ba902e?w=600&h=400&fit=crop&q=80" },
    { title: "Yarra Valley Wine Region", desc: "Day trip to the Yarra Valley, Victoria’s premier wine region. Visit three boutique wineries for tastings of Pinot Noir and Chardonnay. Gourmet lunch at a vineyard restaurant. Stop at the Healesville Sanctuary to see native Australian wildlife — platypus, koalas, and wombats.", schedule: ["Yarra Valley wine region", "Three boutique wineries", "Pinot Noir and Chardonnay tastings", "Vineyard restaurant lunch", "Healesville Sanctuary wildlife"], image: "https://images.unsplash.com/photo-1494949360228-4e9bde560065?w=600&h=400&fit=crop&q=80" },
    { title: "Phillip Island & Penguins", desc: "Afternoon drive to Phillip Island. Visit the Nobbies clifftop boardwalk and the Australian wildlife park. As dusk falls, take your seat at the Penguin Parade — watching hundreds of Little Penguins waddle from the ocean to their burrows is a magical experience. Return to Melbourne late evening.", schedule: ["Drive to Phillip Island", "Nobbies clifftop boardwalk", "Australian wildlife park", "Penguin Parade at dusk", "Return to Melbourne"], image: "https://images.unsplash.com/photo-1514395462725-fb4566210144?w=600&h=400&fit=crop&q=80" },
    { title: "Brighton Bathing Boxes & Departure", desc: "Morning visit to Brighton Beach for photos at the iconic colourful bathing boxes. Final coffee in a St Kilda café along the Esplanade. Transfer to the airport.", schedule: ["Brighton Beach bathing boxes", "St Kilda Esplanade café", "Transfer to airport"], image: "https://images.unsplash.com/photo-1545044846-351ba102b6d5?w=600&h=400&fit=crop&q=80" },
  ],
  "abu dhabi": [
    { title: "Arrival & Corniche", desc: "Arrive at Abu Dhabi International Airport and transfer to your hotel. Evening walk along the Corniche — 8 kilometres of waterfront promenade with the city skyline glittering across the bay. Dinner overlooking the marina.", schedule: ["Airport transfer to hotel", "Corniche evening walk", "Marina dinner"], image: "https://images.unsplash.com/photo-1512632578888-169bbbc64f33?w=600&h=400&fit=crop&q=80" },
    { title: "Sheikh Zayed Grand Mosque & Culture", desc: "Morning at the Sheikh Zayed Grand Mosque — 82 white marble domes, gold-plated chandeliers, and the world's largest hand-knotted carpet. An architectural masterpiece that leaves visitors speechless. Afternoon at the Louvre Abu Dhabi — the 'museum of museums' floating under a latticed dome with light raining through in a 'rain of light' effect. Evening on Saadiyat Beach.", schedule: ["Sheikh Zayed Grand Mosque", "Louvre Abu Dhabi", "Saadiyat Beach evening"], image: "https://images.unsplash.com/photo-1584551246679-0daf3d275d0f?w=600&h=400&fit=crop&q=80" },
    { title: "Yas Island Adventures", desc: "Full day on Yas Island. Morning at Ferrari World — the world's fastest roller coaster. Afternoon at Yas Waterworld or Warner Bros. World. Evening at the Yas Marina Circuit — the same track used for the Abu Dhabi Grand Prix. Dinner at the Yas waterfront.", schedule: ["Ferrari World", "Yas Waterworld or Warner Bros.", "Yas Marina Circuit", "Yas waterfront dinner"], image: "https://images.unsplash.com/photo-1578774204375-826dc5d996ed?w=600&h=400&fit=crop&q=80" },
    { title: "Desert & Mangroves", desc: "Morning kayaking through the Eastern Mangroves — a peaceful paddle through natural tunnels of green. Afternoon desert safari into the Liwa oasis area — dune bashing, sandboarding, and camel rides. Evening at a Bedouin camp under the desert stars with Arabic coffee, dates, and ouzi (slow-roasted lamb).", schedule: ["Eastern Mangroves kayaking", "Liwa desert safari", "Dune bashing and sandboarding", "Bedouin camp evening"], image: "https://images.unsplash.com/photo-1512632578888-169bbbc64f33?w=600&h=400&fit=crop&q=80" },
    { title: "Al Ain Oasis & Departure", desc: "Morning excursion to the Al Ain Oasis — a UNESCO World Heritage Site of 147,000 date palms irrigated by an ancient falaj system. Visit Jebel Hafeet summit for panoramic views of the desert. Return to Abu Dhabi and transfer to the airport.", schedule: ["Al Ain Oasis UNESCO site", "Jebel Hafeet summit", "Transfer to airport"], image: "https://images.unsplash.com/photo-1542314831-068cd1dbfeeb?w=600&h=400&fit=crop&q=80" },
  ],
  kyoto: [
    { title: "Arrival & Gion at Dusk", desc: "Arrive via bullet train from Tokyo or flight to Kansai. Transfer to your ryokan (traditional inn) or hotel. Evening walk through the Gion geisha district — stone-paved Hanamikoji street, wooden machiya houses, paper lanterns, and if you're fortunate, a glimpse of a geiko or maiko hurrying to an appointment.", schedule: ["Bullet train or flight arrival", "Ryokan check-in", "Gion evening walk", "Hanamikoji street"], image: "https://images.unsplash.com/photo-1478436127897-769e1b3f0f36?w=600&h=400&fit=crop&q=80" },
    { title: "Thousand Gates & Golden Pavilion", desc: "Morning at Fushimi Inari Shrine — walk through the seemingly endless tunnel of 10,000 vermillion torii gates winding up the mountainside. Continue to Kinkaku-ji — the Golden Pavilion reflected in its mirror pond. Afternoon in the Nishiki Market — Kyoto's 400-year-old kitchen with over 100 food vendors. Evening kaiseki dinner at a traditional restaurant.", schedule: ["Fushimi Inari Shrine torii gates", "Kinkaku-ji Golden Pavilion", "Nishiki Market", "Kaiseki dinner"], image: "https://images.unsplash.com/photo-1545569341-9eb8b30979d9?w=600&h=400&fit=crop&q=80" },
    { title: "Arashiyama & Bamboo", desc: "Morning in Arashiyama — walk through the towering bamboo grove, cross the Togetsukyo Bridge, and visit Tenryu-ji Temple's Zen garden. Take the scenic Hozugawa River boat ride through forested gorges. Afternoon at Okochi Sanso villa garden with mountain views. Evening tea ceremony experience.", schedule: ["Arashiyama bamboo grove", "Togetsukyo Bridge", "Tenryu-ji Zen garden", "Hozugawa River boat ride", "Tea ceremony"], image: "https://images.unsplash.com/photo-1528360983277-13d401cdc186?w=600&h=400&fit=crop&q=80" },
    { title: "Zen Gardens & Craft", desc: "Morning at Ryoan-ji — Japan's most famous Zen rock garden, contemplative and mesmerising. Continue to the philosopher's path along a cherry-tree-lined canal to Ginkaku-ji (Silver Pavilion). Afternoon indigo dyeing workshop or pottery class in a Kyoto artisan studio. Evening wandering the atmospheric Pontocho alley along the Kamo River.", schedule: ["Ryoan-ji Zen rock garden", "Philosopher's Path", "Ginkaku-ji Silver Pavilion", "Artisan workshop", "Pontocho alley"], image: "https://images.unsplash.com/photo-1590253230532-a67f6bc61c9e?w=600&h=400&fit=crop&q=80" },
    { title: "Nara Day Trip & Departure", desc: "Morning excursion to nearby Nara — bow to the hundreds of sacred deer in the park, visit Todai-ji Temple housing the world's largest bronze Buddha, and explore the serene Kasuga Taisha shrine with its thousands of stone lanterns. Return to Kyoto and depart.", schedule: ["Nara deer park", "Todai-ji Temple bronze Buddha", "Kasuga Taisha shrine", "Return to Kyoto and depart"], image: "https://images.unsplash.com/photo-1504198266287-1659872e6590?w=600&h=400&fit=crop&q=80" },
  ],
  seoul: [
    { title: "Arrival & Myeongdong Neon", desc: "Arrive at Incheon International and transfer to your hotel. Evening in Myeongdong — Seoul's neon-lit beauty and shopping district. Sample street food: hotteok (sweet pancakes), tteokbokki (spicy rice cakes), and Korean fried chicken. K-beauty shopping at flagship stores.", schedule: ["Airport transfer to hotel", "Myeongdong evening", "Korean street food", "K-beauty shopping"], image: "https://images.unsplash.com/photo-1534274867514-d5b47ef89ed7?w=600&h=400&fit=crop&q=80" },
    { title: "Palaces & Tradition", desc: "Morning at Gyeongbokgung Palace — rent a hanbok (traditional dress) for free palace entry and stunning photos against the traditional architecture. Watch the Royal Guard Changing Ceremony. Walk to Bukchon Hanok Village for its alley of traditional Korean houses. Afternoon at Insadong for art galleries, tea houses, and artisan crafts.", schedule: ["Gyeongbokgung Palace in hanbok", "Royal Guard Changing Ceremony", "Bukchon Hanok Village", "Insadong galleries and tea houses"], image: "https://images.unsplash.com/photo-1517154421773-0529f29ea451?w=600&h=400&fit=crop&q=80" },
    { title: "DMZ & North Korean Border", desc: "Full-day excursion to the DMZ — the most heavily fortified border in the world. Visit the Joint Security Area (JSA) where you can step into North Korean territory, the Third Tunnel of Aggression, and the Dora Observatory. A sobering and fascinating experience. Return to Seoul for a Korean BBQ feast in Gangnam.", schedule: ["DMZ full-day excursion", "Joint Security Area", "Third Tunnel of Aggression", "Dora Observatory", "Gangnam Korean BBQ"], image: "https://images.unsplash.com/photo-1506816561089-5cc37b3aa9b0?w=600&h=400&fit=crop&q=80" },
    { title: "Modern Seoul & K-Culture", desc: "Morning at the COEX Starfield Library — a stunning Instagram-worthy space. Explore Gangnam's modern side. Afternoon in Hongdae — the university district buzzing with indie music, street performers, and quirky cafés. Evening at a K-pop merchandise store and a noraebang (Korean karaoke room) — an essential Seoul experience.", schedule: ["COEX Starfield Library", "Gangnam exploration", "Hongdae district", "K-pop store", "Noraebang karaoke"], image: "https://images.unsplash.com/photo-1538485399081-7191377e8241?w=600&h=400&fit=crop&q=80" },
    { title: "Namsan Tower & Departure", desc: "Morning hike or cable car to Namsan Tower (N Seoul Tower) — the love locks and panoramic views are iconic. Explore Itaewon's international food scene for a final brunch. Transfer to the airport.", schedule: ["Namsan Tower hike or cable car", "Itaewon brunch", "Transfer to airport"], image: "https://images.unsplash.com/photo-1601621915196-2621bfb0cd6e?w=600&h=400&fit=crop&q=80" },
  ],
  "kuala lumpur": [
    { title: "Arrival in KL", desc: "Take the KLIA Ekspres from the airport to KL Sentral in 28 minutes, one of Southeast Asia's fastest airport links. Head to the KLCC area at night when the Petronas Twin Towers are illuminated, then dive into Jalan Alor's famous street food stalls for satay, char kway teow, and fresh coconut.", schedule: ["Airport transfer", "Hotel check-in", "KLCC evening", "Street food dinner"], image: "https://images.unsplash.com/photo-1508062878650-88b52897f298?w=600&h=400&fit=crop&q=80" },
    { title: "Petronas & City", desc: "Book Petronas Twin Towers Skybridge tickets online (they sell out fast) for the 41st-floor walkway between the 452-metre towers — the views are dizzying. Stroll through the lush KLCC Park with its splash fountains, shop at Pavilion KL, then ride the lift to the Menara KL Tower's observation deck for a different perspective of the city at sunset.", schedule: ["Breakfast at hotel", "Petronas Twin Towers", "KLCC Park", "Lunch at Pavilion", "KL Tower", "Bukit Bintang", "Rooftop bar sunset"], image: "https://images.unsplash.com/photo-1596422846543-75c6fc197f07?w=600&h=400&fit=crop&q=80" },
    { title: "Culture & Heritage", desc: "Climb the 272 rainbow-painted steps to the Batu Caves, a 400-million-year-old limestone cathedral housing Hindu shrines guarded by a 42-metre golden Murugan statue. Experience KL's multicultural soul — Little India's flower garland sellers, Chinatown's bustling Petaling Street market, and the Moorish-style Sultan Abdul Samad Building on Merdeka Square.", schedule: ["Breakfast at hotel", "Batu Caves", "Little India", "Central Market", "Chinatown lunch", "Merdeka Square", "Traditional dinner"], image: "https://images.unsplash.com/photo-1596402184320-417e7178b2cd?w=600&h=400&fit=crop&q=80" },
    { title: "Day Trip", desc: "Head to the Genting Highlands (1 hour by car) and ride the cable car to the hilltop resort at 1,800 metres for cool mountain air and theme parks. Alternatively, visit the futuristic federal capital of Putrajaya with its pink-domed Putra Mosque and botanical gardens, then return for a final feast on Jalan Alor.", schedule: ["Early breakfast", "Genting Highlands or Putrajaya", "Explore and activities", "Lunch", "Return to KL", "Jalan Alor food street", "Farewell dinner"], image: "https://images.unsplash.com/photo-1596422846543-75c6fc197f07?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Enjoy a final roti canai and teh tarik (pulled tea) breakfast before heading to the airport. KLIA's duty-free has excellent deals on Beryl's chocolate and local batik.", schedule: ["Final breakfast", "Last shopping", "Transfer to KLIA"], image: "https://images.unsplash.com/photo-1580481072645-022f9a6dbf27?w=600&h=400&fit=crop&q=80" },
  ],
  "vietnam explorer": [
    { title: "Arrival in Hanoi", desc: "Arrive at Noi Bai International Airport and transfer to your hotel in the Old Quarter. Evening walk through the 36 streets of the Old Quarter — each named for the trade practised there. First bowl of pho at a tiny street stall, sitting on child-sized plastic stools.", schedule: ["Airport transfer to Old Quarter", "36 streets evening walk", "First pho street stall"], image: "https://images.unsplash.com/photo-1557750255-c76072a7aad1?w=600&h=400&fit=crop&q=80" },
    { title: "Hanoi Heritage", desc: "Morning at the Ho Chi Minh Mausoleum, One Pillar Pagoda, and the Temple of Literature — Vietnam's first university, dating to 1070. Afternoon cyclo ride through the French Quarter past the Opera House. Visit Hoa Lo Prison (the 'Hanoi Hilton') for a compelling war history lesson. Evening water puppet show at Thang Long Theatre.", schedule: ["Ho Chi Minh Mausoleum", "Temple of Literature", "French Quarter cyclo ride", "Hoa Lo Prison", "Water puppet show"], image: "https://images.unsplash.com/photo-1583417319070-4a69db38a482?w=600&h=400&fit=crop&q=80" },
    { title: "Ha Long Bay Overnight", desc: "Transfer to Ha Long Bay and board a traditional junk boat for an overnight cruise. Sail through thousands of limestone karsts rising from emerald water. Kayak through caves and floating villages. Sunset on the deck with fresh seafood dinner as the bay turns golden.", schedule: ["Transfer to Ha Long Bay", "Junk boat overnight cruise", "Limestone karst sailing", "Cave kayaking", "Sunset deck dinner"], image: "https://images.unsplash.com/photo-1528127269322-539801943592?w=600&h=400&fit=crop&q=80" },
    { title: "Ha Long Bay & Flight to Hoi An", desc: "Sunrise tai chi on the deck. Morning exploring a cave system. Return to shore and fly to Da Nang, then transfer to Hoi An. Evening walk through the Ancient Town — hundreds of silk lanterns reflected in the Thu Bon River, the Japanese Covered Bridge lit up, and tailors open late.", schedule: ["Sunrise tai chi", "Cave exploration", "Flight to Da Nang", "Hoi An Ancient Town evening"], image: "https://images.unsplash.com/photo-1513415564515-763d91423bdd?w=600&h=400&fit=crop&q=80" },
    { title: "Hoi An & Tailoring", desc: "Morning cooking class at a riverside restaurant — market tour, boat ride through the water coconut forest, then learn to make bánh xèo and fresh spring rolls. Afternoon visiting a tailor for custom-made clothing (24-hour turnaround). Explore the Chinese Assembly Halls and merchant houses. Sunset at An Bàng Beach.", schedule: ["Riverside cooking class", "Market tour", "Tailor visit", "Chinese Assembly Halls", "An Bàng Beach sunset"], image: "https://images.unsplash.com/photo-1535086181678-5a5c4d23aa7d?w=600&h=400&fit=crop&q=80" },
    { title: "Hue Imperial City", desc: "Day trip to Hue — the former imperial capital. Explore the Citadel and Forbidden Purple City, cruise the Perfume River past royal tombs, and visit the ornate Khai Dinh Mausoleum. Return to Hoi An for a final night market stroll.", schedule: ["Hue Citadel", "Forbidden Purple City", "Perfume River cruise", "Khai Dinh Mausoleum", "Hoi An night market"], image: "https://images.unsplash.com/photo-1557750255-c76072a7aad1?w=600&h=400&fit=crop&q=80" },
    { title: "Beach & Departure", desc: "Final morning at An Bàng Beach. Pick up your tailored clothes. Transfer to Da Nang Airport for departure.", schedule: ["An Bàng Beach morning", "Collect tailored clothes", "Transfer to Da Nang Airport"], image: "https://images.unsplash.com/photo-1583417319070-4a69db38a482?w=600&h=400&fit=crop&q=80" },
  ],
  florence: [
    { title: "Arrival in Florence", desc: "Take the tramway from Florence airport to the city centre in 20 minutes, or arrive by high-speed train from Rome (1.5 hours) at Santa Maria Novella station. Walk to the Ponte Vecchio at sunset when the medieval goldsmith shops glow over the Arno River, then enjoy a bistecca alla fiorentina at a traditional Oltrarno trattoria.", schedule: ["Airport/station transfer", "Hotel check-in", "Ponte Vecchio evening", "Tuscan dinner"], image: "https://images.unsplash.com/photo-1541370976299-4d24ebbc9077?w=600&h=400&fit=crop&q=80" },
    { title: "Renaissance Art", desc: "Book timed Uffizi tickets to see Botticelli's Birth of Venus and Caravaggio's Medusa without the crowds — allow at least 3 hours for the collection. Walk to the Duomo and climb Brunelleschi's dome via 463 narrow steps for bird's-eye views across Florence's terracotta rooftops to the surrounding Tuscan hills.", schedule: ["Breakfast at hotel", "Uffizi Gallery", "Piazza della Signoria", "Lunch near Duomo", "Florence Cathedral climb", "Baptistery", "Gelato and evening walk"], image: "https://images.unsplash.com/photo-1543429258-c5ca3e1b6a19?w=600&h=400&fit=crop&q=80" },
    { title: "Accademia & Craftsmen", desc: "See Michelangelo's David at the Accademia Gallery — the 5.17-metre marble masterpiece is even more impressive in person than in photos. Cross the river to Oltrarno, Florence's artisan quarter, to watch leather craftsmen, bookbinders, and mosaic artists at work in their workshops, then hike up to Piazzale Michelangelo for the city's most famous panoramic sunset view.", schedule: ["Breakfast at hotel", "Accademia Gallery", "Michelangelo's David", "Santa Croce", "Lunch in Oltrarno", "Artisan workshops", "Piazzale Michelangelo sunset"], image: "https://images.unsplash.com/photo-1544211726-99e2512c9832?w=600&h=400&fit=crop&q=80" },
    { title: "Tuscany Day Trip", desc: "Drive through the rolling Chianti hills dotted with cypress trees and medieval villages for wine and olive oil tastings at family-run estates. Visit the towered skyline of San Gimignano ('Medieval Manhattan') or the fan-shaped Piazza del Campo in Siena, then return through the golden Tuscan countryside for a farewell dinner.", schedule: ["Early breakfast", "Chianti wine tour", "Medieval villages", "Wine and olive oil tasting", "Tuscan lunch", "San Gimignano or Siena", "Return and farewell dinner"], image: "https://images.unsplash.com/photo-1516483638261-f4dbaf036963?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Savour a final cornetto and cappuccino at a neighbourhood bar and pick up Florentine leather goods, truffle products, or a bottle of Chianti Classico before heading to the airport or station.", schedule: ["Final breakfast", "Last leather shopping", "Transfer to station/airport"], image: "https://images.unsplash.com/photo-1476362555312-ab9e108a0b7e?w=600&h=400&fit=crop&q=80" },
  ],
  dublin: [
    { title: "Arrival in Dublin", desc: "Take the Airlink Express bus from Dublin airport to the city centre in 30 minutes. Head straight to the cobbled streets of Temple Bar, Dublin's cultural quarter, for live traditional music pouring from every pub doorway and your first pint of Guinness where it tastes best — in Ireland.", schedule: ["Airport transfer", "Hotel check-in", "Temple Bar evening", "Irish pub dinner"], image: "https://images.unsplash.com/photo-1549918864-48ac978761a4?w=600&h=400&fit=crop&q=80" },
    { title: "City Highlights", desc: "Visit Trinity College to see the 1,200-year-old Book of Kells, Ireland's most famous medieval manuscript, in its stunning Long Room library — arrive at opening time as queues build quickly. Walk Grafton Street's busker-filled pedestrian strip, explore St. Patrick's Cathedral (founded 1191), and end with a traditional Irish music session in a Doyle's or O'Neill's pub.", schedule: ["Breakfast at hotel", "Trinity College", "Book of Kells", "Grafton Street", "St. Patrick's Cathedral", "Dublin Castle", "Traditional music evening"], image: "https://images.unsplash.com/photo-1565791380713-1756b9a05343?w=600&h=400&fit=crop&q=80" },
    { title: "Guinness & Culture", desc: "Tour the Guinness Storehouse, Ireland's most-visited attraction, and learn to pour the perfect pint at the rooftop Gravity Bar with 360-degree views over Dublin. Visit the Jameson Distillery for a whiskey tasting, then stroll through the 707-hectare Phoenix Park — one of Europe's largest urban parks, home to a herd of wild fallow deer.", schedule: ["Breakfast at hotel", "Guinness Storehouse", "Lunch overlooking city", "Jameson Distillery", "Phoenix Park", "Ha'penny Bridge", "Literary pub crawl"], image: "https://images.unsplash.com/photo-1590071089561-8c8e5e12e637?w=600&h=400&fit=crop&q=80" },
    { title: "Coastal Escape", desc: "Take the 25-minute DART train to Howth, a charming fishing village on a rocky peninsula. Walk the dramatic cliff loop trail (6km, about 2 hours) with views across Dublin Bay, then feast on the freshest fish and chips at the harbour. Return for a final sunset at the trendy Grand Canal Dock area.", schedule: ["Breakfast at hotel", "Howth fishing village", "Cliff walk", "Seafood lunch", "EPIC Irish Emigration Museum", "Sunset at Grand Canal Dock", "Farewell dinner"], image: "https://images.unsplash.com/photo-1564959130747-897a8e4d4435?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Enjoy a full Irish breakfast (rashers, black pudding, soda bread) and pick up Aran knit jumpers, Claddagh rings, or a bottle of Redbreast whiskey before heading to the airport.", schedule: ["Final breakfast", "Last Temple Bar visit", "Transfer to airport"], image: "https://images.unsplash.com/photo-1548337138-e87d889cc369?w=600&h=400&fit=crop&q=80" },
  ],
  edinburgh: [
    { title: "Arrival in Edinburgh", desc: "Take the Airlink 100 bus from Edinburgh airport to the city centre in 25 minutes. Walk the Royal Mile from Edinburgh Castle down to Holyrood Palace as the medieval Old Town's towers and closes (narrow alleyways) come alive in the evening light, then settle into a cosy pub for haggis, neeps, and tatties.", schedule: ["Airport transfer", "Hotel check-in", "Royal Mile evening walk", "Scottish dinner"], image: "https://images.unsplash.com/photo-1506377711776-dbdc2f3c20d9?w=600&h=400&fit=crop&q=80" },
    { title: "Castle & Old Town", desc: "Explore Edinburgh Castle perched atop the volcanic Castle Rock with panoramic views across the city to the Firth of Forth. Walk the Royal Mile past St. Giles' Cathedral and descend into the underground Real Mary King's Close for a ghostly tour of 17th-century plague-era streets, then warm up with a single malt at a Scotch whisky experience.", schedule: ["Breakfast at hotel", "Edinburgh Castle", "Royal Mile exploration", "St. Giles' Cathedral", "Lunch on Grassmarket", "Mary King's Close", "Whisky tasting", "Ghost tour evening"], image: "https://images.unsplash.com/photo-1566041510394-cf7c8fe21800?w=600&h=400&fit=crop&q=80" },
    { title: "Arthur's Seat & New Town", desc: "Hike to the summit of Arthur's Seat, an ancient volcano rising 251 metres in the heart of the city — the 45-minute climb rewards you with sweeping views from the castle to the sea. Visit the Palace of Holyroodhouse, the Queen's official Scottish residence, then stroll the Georgian elegance of the New Town and browse the Scottish National Gallery's collection.", schedule: ["Breakfast at hotel", "Climb Arthur's Seat", "Holyrood Palace", "Lunch", "New Town walk", "Scottish National Gallery", "Princes Street shopping", "Fine dining"], image: "https://images.unsplash.com/photo-1600973036264-bf2e28d42f89?w=600&h=400&fit=crop&q=80" },
    { title: "Highlands Taste", desc: "Drive 40 minutes to Stirling Castle, the 'key to Scotland' where Mary Queen of Scots was crowned, or visit the mysterious Rosslyn Chapel made famous by The Da Vinci Code. Continue into the Scottish countryside for a glimpse of Highland lochs and rolling glens before returning for a farewell dinner of fresh Scottish salmon and whisky.", schedule: ["Early breakfast", "Stirling Castle or Rosslyn Chapel", "Scottish countryside", "Highland lunch", "Loch views", "Return to Edinburgh", "Farewell dinner"], image: "https://images.unsplash.com/photo-1506377585622-bedcbb027afc?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Pick up a bottle of single malt, Edinburgh fudge, or a tartan scarf from the Royal Mile shops before heading to the airport. The Airlink bus runs every 10 minutes from Waverley Bridge.", schedule: ["Final breakfast", "Last Royal Mile walk", "Transfer to airport"], image: "https://images.unsplash.com/photo-1566041510394-cf7c8fe21800?w=600&h=400&fit=crop&q=80" },
  ],
  budapest: [
    { title: "Arrival in Budapest", desc: "Take the airport shuttle bus 100E to the city centre in 35 minutes. Walk along the illuminated Danube riverbanks (a UNESCO site) past the Chain Bridge and Parliament Building, then head to a 'ruin bar' — Budapest's famous bars built inside crumbling courtyards, Szimpla Kert being the original and most iconic.", schedule: ["Airport transfer", "Hotel check-in", "Danube evening walk", "Ruin bar dinner"], image: "https://images.unsplash.com/photo-1565426873118-a17ed65d74b9?w=600&h=400&fit=crop&q=80" },
    { title: "Buda Castle District", desc: "Cross the Chain Bridge to the Buda side and climb to the fairytale turrets of Fisherman's Bastion for the best panoramic view of Parliament and Pest across the river. Explore the 13th-century Matthias Church with its colourful Zsolnay-tiled roof, then soak in the Art Nouveau Gellert Thermal Baths where Budapest's famous thermal springs have been flowing for 2,000 years.", schedule: ["Breakfast at hotel", "Fisherman's Bastion", "Matthias Church", "Buda Castle", "Lunch with views", "Gellert Hill sunset", "Thermal bath evening"], image: "https://images.unsplash.com/photo-1565426873118-a17ed65d74b9?w=600&h=400&fit=crop&q=80" },
    { title: "Pest Highlights", desc: "Tour the Hungarian Parliament Building, the world's third-largest parliament with its 691 ornately decorated rooms and the Holy Crown of Hungary on display. Pay respects at the haunting Shoes on the Danube memorial, climb St. Stephen's Basilica's dome for city views, and browse the vibrant Central Market Hall's paprika, salami, and lace stalls.", schedule: ["Breakfast at hotel", "Parliament Building", "Shoes on the Danube", "St. Stephen's Basilica", "Central Market lunch", "Jewish Quarter", "Ruin bars evening"], image: "https://images.unsplash.com/photo-1541300613939-71366b37c92e?w=600&h=400&fit=crop&q=80" },
    { title: "Baths & Culture", desc: "Spend the morning at the grand Szechenyi Thermal Bath, one of Europe's largest public bath complexes, with its Neo-Baroque outdoor pools steaming in the morning air. Walk through Heroes' Square and City Park to the Museum of Fine Arts, then attend a performance at the stunning State Opera House — standing tickets are just a few euros.", schedule: ["Breakfast at hotel", "Szechenyi Thermal Bath", "Heroes' Square", "City Park", "Lunch", "Museum of Fine Arts", "Opera House", "Farewell dinner"], image: "https://images.unsplash.com/photo-1551867633-194f125bddfa?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Enjoy a final langos (fried dough with sour cream) and pick up paprika, Tokaji wine, or Hungarian embroidery before heading to the airport. The 100E bus runs every 20 minutes from Deak Ferenc ter.", schedule: ["Final breakfast", "Last Danube views", "Transfer to airport"], image: "https://images.unsplash.com/photo-1551867633-194f125bddfa?w=600&h=400&fit=crop&q=80" },
  ],
  "nice & french riviera": [
    { title: "Arrival in Nice", desc: "Nice airport is just 6km from the city centre — the tram connects you in 30 minutes. Walk the legendary Promenade des Anglais along the azure Mediterranean, then dive into the narrow lanes of Vieux Nice (Old Town) for socca (chickpea pancake), salade nicoise, and a glass of chilled Provencal rose.", schedule: ["Airport transfer", "Hotel check-in", "Promenade des Anglais walk", "Dinner in Old Town"], image: "https://images.unsplash.com/photo-1585208798174-6cedd86e019a?w=600&h=400&fit=crop&q=80" },
    { title: "Nice & Monaco", desc: "Browse the vibrant Cours Saleya flower and food market in Old Nice, then take the 20-minute coastal train to Monaco. Walk past the superyachts in Port Hercules, peer into the opulent Monte Carlo Casino (entry from EUR 17), and visit the Prince's Palace at noon for the ceremonial guard change.", schedule: ["Breakfast at hotel", "Vieux Nice exploration", "Cours Saleya market", "Lunch in Nice", "Train to Monaco", "Monte Carlo Casino", "Prince's Palace", "Dinner in Monaco"], image: "https://images.unsplash.com/photo-1504893524553-b855bce32c67?w=600&h=400&fit=crop&q=80" },
    { title: "Cannes & Antibes", desc: "Take the 30-minute train to Cannes and walk La Croisette, the famous palm-lined promenade where film stars stroll during the festival. Continue to medieval Antibes for the Picasso Museum housed in a 12th-century chateau overlooking the sea, then walk the coastal path around Cap d'Antibes for stunning views of the Alps meeting the Mediterranean.", schedule: ["Breakfast at hotel", "Train to Cannes", "La Croisette", "Lunch in Cannes", "Antibes old town", "Picasso Museum", "Cap d'Antibes walk", "Return and dinner"], image: "https://images.unsplash.com/photo-1491166617655-0723a0999cfc?w=600&h=400&fit=crop&q=80" },
    { title: "Eze & Beaches", desc: "Visit the medieval hilltop village of Eze, perched 427 metres above the sea like an eagle's nest, and wander the Jardin Exotique cactus garden with breathtaking views across the Cote d'Azur. Spend the afternoon on the pebbly beaches of Villefranche-sur-Mer, then climb Castle Hill in Nice for a farewell sunset over the Baie des Anges.", schedule: ["Breakfast at hotel", "Eze medieval village", "Exotic garden", "Lunch with views", "Beach afternoon", "Castle Hill sunset", "Farewell dinner"], image: "https://images.unsplash.com/photo-1491166617655-0723a0999cfc?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Enjoy a final pan bagnat sandwich and Provencal lavender ice cream before heading to the airport. Pick up olive oil, herbes de Provence, or local perfume from Grasse as Riviera gifts.", schedule: ["Final breakfast", "Last Promenade walk", "Transfer to airport"], image: "https://images.unsplash.com/photo-1533104816931-20fa691ff6ca?w=600&h=400&fit=crop&q=80" },
  ],
  mauritius: [
    { title: "Arrival & Indian Ocean", desc: "Arrive at Sir Seewoosagur Ramgoolam International Airport and transfer to your resort. Afternoon on the beach — warm turquoise water, white sand, casuarina trees. Welcome rum punch at sunset.", schedule: ["Airport transfer to resort", "Beach afternoon", "Welcome rum punch at sunset"], image: "https://images.unsplash.com/photo-1589979481223-deb893043163?w=600&h=400&fit=crop&q=80" },
    { title: "Île aux Cerfs & Water Sports", desc: "Speedboat to Île aux Cerfs — a paradise island with pristine beaches and crystal lagoon. Parasailing, glass-bottom boat rides, or simply floating in the shallows. Seafood barbecue lunch on the island. Return to the resort for evening at leisure.", schedule: ["Speedboat to Île aux Cerfs", "Parasailing or glass-bottom boat", "Seafood barbecue lunch", "Evening at leisure"], image: "https://images.unsplash.com/photo-1586500036706-41963de24d8b?w=600&h=400&fit=crop&q=80" },
    { title: "Chamarel & Black River Gorges", desc: "Full-day nature excursion. Visit the Seven Coloured Earths of Chamarel — striped geological formations in red, brown, purple and blue. Continue to the Chamarel waterfall, then hike through Black River Gorges National Park to spot the rare Mauritius kestrel. Rum distillery visit and tasting at Rhumerie de Chamarel.", schedule: ["Seven Coloured Earths", "Chamarel waterfall", "Black River Gorges hike", "Rhumerie de Chamarel tasting"], image: "https://images.unsplash.com/photo-1585821569331-f071db2abd8d?w=600&h=400&fit=crop&q=80" },
    { title: "Cultural Heritage", desc: "Morning at Grand Bassin — the sacred Hindu crater lake with towering deity statues. Drive through the tea plantations of Bois Chéri for a factory tour and tasting. Afternoon in the colonial town of Mahébourg with its Naval Museum. Evening Sega dance performance on the beach — Mauritius's traditional music and dance.", schedule: ["Grand Bassin Hindu lake", "Bois Chéri tea plantation", "Mahébourg Naval Museum", "Sega beach dance performance"], image: "https://images.unsplash.com/photo-1602002418082-a4443e081dd1?w=600&h=400&fit=crop&q=80" },
    { title: "Underwater Waterfall & Le Morne", desc: "Helicopter or seaplane flight over the southwestern coast to see the incredible underwater waterfall illusion from the air — one of Mauritius's most photographed sights. Afternoon at Le Morne beach, beneath the UNESCO-listed Le Morne Brabant mountain. Sunset from the base of the mountain.", schedule: ["Helicopter or seaplane flight", "Underwater waterfall aerial view", "Le Morne beach", "Le Morne Brabant sunset"], image: "https://images.unsplash.com/photo-1590523741831-ab7e8b8f9c7f?w=600&h=400&fit=crop&q=80" },
    { title: "Port Louis & Street Food", desc: "Morning in the capital Port Louis — the bustling Central Market with its spices, textiles, and exotic fruits. Chinatown for dim sum. Afternoon at the Blue Penny Museum and the Caudan Waterfront. Return to the resort for a final spa treatment and farewell dinner on the beach.", schedule: ["Port Louis Central Market", "Chinatown dim sum", "Blue Penny Museum", "Farewell beach dinner"], image: "https://images.unsplash.com/photo-1589979481223-deb893043163?w=600&h=400&fit=crop&q=80" },
    { title: "Botanical Garden & Departure", desc: "Morning at the Pamplemousses Botanical Garden — giant water lilies, Talipot palms, and a tortoise enclosure. Last swim. Transfer to the airport.", schedule: ["Pamplemousses Botanical Garden", "Last swim", "Transfer to airport"], image: "https://images.unsplash.com/photo-1586500036706-41963de24d8b?w=600&h=400&fit=crop&q=80" },
  ],
  "sri lanka": [
    { title: "Arrival & Negombo", desc: "Arrive at Bandaranaike International Airport and transfer to a beach hotel in Negombo. Afternoon exploring the Dutch-era canal, fish market, and colonial-era fort. First Sri Lankan rice and curry dinner.", schedule: ["Airport transfer to Negombo", "Dutch canal and fish market", "First rice and curry dinner"], image: "https://images.unsplash.com/photo-1544367567-0f2fcb009e0b?w=600&h=400&fit=crop&q=80" },
    { title: "Sigiriya Rock Fortress", desc: "Drive to the Cultural Triangle. Climb the ancient Sigiriya Rock Fortress — 1,200 steps up a 200-metre rock column, past the famous frescoes and the mirror wall, to the ruins of a 5th-century palace at the summit with 360-degree jungle views. Afternoon at Dambulla Cave Temple with its 150 Buddha statues. Overnight in Sigiriya area.", schedule: ["Drive to Cultural Triangle", "Sigiriya Rock Fortress climb", "Summit palace ruins", "Dambulla Cave Temple"], image: "https://images.unsplash.com/photo-1586699253884-e199770f63b9?w=600&h=400&fit=crop&q=80" },
    { title: "Kandy & Temple of the Tooth", desc: "Drive to Kandy through spice gardens and hill country. Visit the Temple of the Tooth — Sri Lanka's most sacred Buddhist relic, housing a tooth of the Buddha. Explore Kandy's lakeside promenade. Evening Kandyan dance performance with fire-walking.", schedule: ["Drive to Kandy", "Temple of the Tooth", "Lakeside promenade", "Kandyan dance performance"], image: "https://images.unsplash.com/photo-1568214379698-8aeb8c6c6ac8?w=600&h=400&fit=crop&q=80" },
    { title: "Train to Ella", desc: "Board the famous Kandy-to-Ella railway — often called the most scenic train ride in the world. Wind through tea plantations, across the Nine Arches Bridge, over waterfalls, and through cloud-wrapped mountains for seven breathtaking hours. Arrive in Ella.", schedule: ["Kandy-to-Ella scenic railway", "Tea plantation views", "Nine Arches Bridge", "Arrive in Ella"], image: "https://images.unsplash.com/photo-1578469550956-0e16b69c6a3d?w=600&h=400&fit=crop&q=80" },
    { title: "Ella & Tea Country", desc: "Morning hike to Little Adam's Peak for panoramic views of the valley. Visit a working tea factory and learn the process from leaf to cup. Afternoon at Ravana Falls for a refreshing swim. Evening at a hilltop café watching the sunset over the highlands.", schedule: ["Little Adam's Peak hike", "Tea factory visit", "Ravana Falls swim", "Hilltop café sunset"], image: "https://images.unsplash.com/photo-1578469550956-0e16b69c6a3d?w=600&h=400&fit=crop&q=80" },
    { title: "Yala National Park Safari", desc: "Pre-dawn departure for a jeep safari in Yala National Park — the highest density of leopards anywhere in the world. Also spot elephants, crocodiles, sloth bears, and hundreds of bird species. Transfer to the southern coast. Afternoon at Mirissa or Unawatuna beach.", schedule: ["Yala National Park jeep safari", "Leopard and elephant spotting", "Transfer to southern coast", "Mirissa or Unawatuna beach"], image: "https://images.unsplash.com/photo-1544367567-0f2fcb009e0b?w=600&h=400&fit=crop&q=80" },
    { title: "Southern Coast & Departure", desc: "Morning watching stilt fishermen at Koggala — an image that defines Sri Lanka. Visit the historic Galle Fort — a UNESCO-listed Dutch colonial fortress with boutique shops and cafés. Transfer to the airport.", schedule: ["Koggala stilt fishermen", "Galle Fort exploration", "Transfer to airport"], image: "https://images.unsplash.com/photo-1586699253884-e199770f63b9?w=600&h=400&fit=crop&q=80" },
  ],
  sicily: [
    { title: "Arrival in Sicily", desc: "Fly into Catania airport on Sicily's eastern coast and transfer to your hotel — the volcanic peak of Mount Etna looms dramatically on the horizon. Join the evening passeggiata (promenade) through the town's baroque piazzas and feast on arancini, fresh pasta alla Norma, and cannoli at a traditional trattoria.", schedule: ["Airport transfer", "Hotel check-in", "Evening passeggiata", "Sicilian dinner"], image: "https://images.unsplash.com/photo-1523531294919-4bcd7c65e216?w=600&h=400&fit=crop&q=80" },
    { title: "Taormina", desc: "Visit Taormina, dramatically perched on a cliff 200 metres above the Ionian Sea, and explore its ancient Greek Theatre (3rd century BC) with Mount Etna as a natural backdrop. Walk the elegant Corso Umberto shopping street, take the cable car down to the jewel-like Isola Bella beach, and dine with panoramic views of the coastline at sunset.", schedule: ["Breakfast at hotel", "Taormina old town", "Greek Theatre", "Lunch with Etna views", "Isola Bella beach", "Corso Umberto shopping", "Dinner at sunset"], image: "https://images.unsplash.com/photo-1560184897-502a475f7a0d?w=600&h=400&fit=crop&q=80" },
    { title: "Mount Etna", desc: "Drive up Europe's most active volcano to the Silvestri Craters at 1,900 metres, where you can walk on lunar-like lava fields and peer into steaming vents. Take the cable car and 4x4 to the summit craters at 3,350 metres (weather permitting), then descend the eastern slopes for a wine tasting on Etna's mineral-rich volcanic vineyards — the Nerello Mascalese reds are exceptional.", schedule: ["Early breakfast", "Drive to Etna", "Crater exploration", "Lava caves", "Wine tasting on slopes", "Return to hotel", "Traditional dinner"], image: "https://images.unsplash.com/photo-1516483638261-f4dbaf036963?w=600&h=400&fit=crop&q=80" },
    { title: "Syracuse", desc: "Explore Syracuse, once the most powerful city in the ancient Greek world — the island of Ortigia is a maze of baroque palaces, ancient temples, and tiny seafood restaurants. Visit the immense Greek Theatre (still used for performances), the eerie Ear of Dionysius limestone cave, and feast on the freshest catch at the Ortigia fish market.", schedule: ["Breakfast at hotel", "Ortigia island", "Greek Theatre Syracuse", "Ear of Dionysius", "Seafood lunch", "Baroque architecture", "Farewell dinner"], image: "https://images.unsplash.com/photo-1515859005217-8a1f08870f59?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Savour a final granita con brioche (Sicilian iced breakfast) and pick up pistachio paste from Bronte, Modica chocolate, or Marsala wine as gifts before transferring to Catania airport.", schedule: ["Final breakfast", "Last espresso", "Transfer to airport"], image: "https://images.unsplash.com/photo-1534308983496-4fabb1a015ee?w=600&h=400&fit=crop&q=80" },
  ],
  iceland: [
    { title: "Arrival & Blue Lagoon", desc: "Land at Keflavík International Airport. Drive directly to the Blue Lagoon — soak in the milky-blue geothermal water surrounded by black lava fields. Transfer to Reykjavik. Evening walk through the colourful Laugavegur shopping street and dinner at a restaurant serving Icelandic lamb.", schedule: ["Keflavík Airport arrival", "Blue Lagoon soak", "Transfer to Reykjavik", "Laugavegur evening walk"], image: "https://images.unsplash.com/photo-1529963183134-61a90db47eaf?w=600&h=400&fit=crop&q=80" },
    { title: "Golden Circle", desc: "Full-day Golden Circle route. Þingvellir National Park — stand between the North American and Eurasian tectonic plates. Geysir geothermal area — watch Strokkur erupt every few minutes. Gullfoss waterfall — a thundering two-tier cascade into a canyon. Optional afternoon snorkelling in Silfra fissure with cathedral-like underwater visibility.", schedule: ["Þingvellir National Park", "Geysir and Strokkur", "Gullfoss waterfall", "Optional Silfra snorkelling"], image: "https://images.unsplash.com/photo-1520769945061-0a448c463865?w=600&h=400&fit=crop&q=80" },
    { title: "South Coast Waterfalls & Black Beach", desc: "Drive the south coast. Stop at Seljalandsfoss — walk behind the curtain of water for a unique perspective. Continue to Skógafoss, a 60-metre wall of water you can climb beside. Afternoon at Reynisfjara black sand beach — dramatic basalt columns and crashing Atlantic waves. Overnight in Vík.", schedule: ["Seljalandsfoss waterfall", "Skógafoss waterfall", "Reynisfjara black sand beach", "Overnight in Vík"], image: "https://images.unsplash.com/photo-1494500764479-0c8f2919a3d8?w=600&h=400&fit=crop&q=80" },
    { title: "Glacier Lagoon & Ice Cave", desc: "Continue east to Jökulsárlón glacier lagoon — icebergs calving from the Vatnajökull glacier float across a lagoon to the sea. Walk the Diamond Beach where ice chunks wash up on black sand like giant jewels. Afternoon ice cave exploration inside the glacier (seasonal) or a glacier hike.", schedule: ["Jökulsárlón glacier lagoon", "Diamond Beach", "Ice cave or glacier hike"], image: "https://images.unsplash.com/photo-1515238152791-8216bfdf89a7?w=600&h=400&fit=crop&q=80" },
    { title: "East Fjords & Highlands", desc: "Drive through the remote East Fjords — fishing villages, wild reindeer, dramatic cliff roads. Stop at Seyðisfjörður, the most photogenic town in eastern Iceland with its rainbow-painted church and waterfall backdrop. Continue to Egilsstaðir.", schedule: ["East Fjords drive", "Seyðisfjörður town", "Continue to Egilsstaðir"], image: "https://images.unsplash.com/photo-1504829857797-ddff29c27927?w=600&h=400&fit=crop&q=80" },
    { title: "Mývatn & North Iceland", desc: "Cross to the Mývatn area — otherworldly lava formations at Dimmuborgir, the Grjótagjá lava cave, and the Mývatn Nature Baths (a quieter alternative to the Blue Lagoon). Visit Goðafoss — the 'waterfall of the gods'. Afternoon whale watching from Húsavík — humpback whales are common.", schedule: ["Dimmuborgir lava formations", "Mývatn Nature Baths", "Goðafoss waterfall", "Húsavík whale watching"], image: "https://images.unsplash.com/photo-1529963183134-61a90db47eaf?w=600&h=400&fit=crop&q=80" },
    { title: "Northern Lights & Departure", desc: "Morning flight from Akureyri back to Reykjavik (or drive the ring road back). Depending on season, a northern lights hunt the previous evening. Final Icelandic hot dog at the famous Bæjarins Beztu stand. Transfer to the airport.", schedule: ["Flight to Reykjavik", "Northern lights hunt (seasonal)", "Final Icelandic hot dog", "Transfer to airport"], image: "https://images.unsplash.com/photo-1520769945061-0a448c463865?w=600&h=400&fit=crop&q=80" },
  ],
  zanzibar: [
    { title: "Arrival & Stone Town", desc: "Arrive at Abeid Amani Karume Airport and transfer to your hotel in Stone Town. Evening walk through the UNESCO-listed streets — intricately carved wooden doors, hidden courtyards, and the Forodhani Gardens night food market where vendors grill fresh seafood, Zanzibar pizza, and sugarcane juice over charcoal.", schedule: ["Airport transfer to Stone Town", "UNESCO streets evening walk", "Forodhani Gardens food market"], image: "https://images.unsplash.com/photo-1547471080-7cc2caa01a7e?w=600&h=400&fit=crop&q=80" },
    { title: "Stone Town Heritage", desc: "Guided walking tour of Stone Town's cultural landmarks — the House of Wonders, the Old Fort, the Anglican Cathedral built on the former slave market site, and Freddie Mercury's birthplace. Browse the Darajani Market for exotic fruits and spices. Afternoon rooftop sundowners overlooking the Indian Ocean.", schedule: ["House of Wonders", "Old Fort", "Anglican Cathedral", "Freddie Mercury's birthplace", "Darajani Market", "Rooftop sundowners"], image: "https://images.unsplash.com/photo-1590523278191-995a80f80007?w=600&h=400&fit=crop&q=80" },
    { title: "Spice Farm Tour", desc: "Morning at a spice plantation — Zanzibar earned its name as the Spice Island. See cloves, nutmeg, cinnamon, vanilla, and lemongrass growing on the vine. Taste fresh spices and tropical fruits straight from the tree. Afternoon transfer to a beach resort on the north or east coast for the remainder of the trip.", schedule: ["Spice plantation tour", "Fresh spice tasting", "Transfer to beach resort"], image: "https://images.unsplash.com/photo-1586699253884-e199770f63b9?w=600&h=400&fit=crop&q=80" },
    { title: "Mnemba Atoll Snorkelling", desc: "Dhow boat trip to Mnemba Atoll — a pristine marine conservation area. Snorkel in crystal-clear water among tropical fish, dolphins, and green sea turtles. Picnic lunch on the dhow. Afternoon at the beach, where the tide retreats hundreds of metres revealing sandbanks.", schedule: ["Dhow to Mnemba Atoll", "Marine conservation snorkelling", "Dhow picnic lunch", "Beach and sandbanks"], image: "https://images.unsplash.com/photo-1682687982501-1e58ab814714?w=600&h=400&fit=crop&q=80" },
    { title: "Jozani Forest & Mangroves", desc: "Morning visit to Jozani Chwaka Bay National Park — home to the rare red colobus monkey found nowhere else on earth. Walk the mangrove boardwalk through the tidal forest. Afternoon at leisure. Sunset from The Rock restaurant — the iconic Indian Ocean restaurant perched on a coral rock.", schedule: ["Jozani red colobus monkeys", "Mangrove boardwalk", "Afternoon at leisure", "The Rock restaurant sunset"], image: "https://images.unsplash.com/photo-1547471080-7cc2caa01a7e?w=600&h=400&fit=crop&q=80" },
    { title: "Dhow Sunset Cruise", desc: "Morning relaxation and beach time. Afternoon traditional dhow sunset cruise along the coast — watch the sail fill with wind as the sky turns orange and pink over the Indian Ocean. Farewell dinner on the beach under the stars.", schedule: ["Morning beach time", "Dhow sunset cruise", "Farewell beach dinner under stars"], image: "https://images.unsplash.com/photo-1547471080-7cc2caa01a7e?w=600&h=400&fit=crop&q=80" },
    { title: "Low Tide Walk & Departure", desc: "Early morning walk on the exposed sand flats at low tide — local women harvesting seaweed, children playing, and the ocean a distant shimmer. Last swim. Transfer to the airport.", schedule: ["Low tide sand flat walk", "Last swim", "Transfer to airport"], image: "https://images.unsplash.com/photo-1590523278191-995a80f80007?w=600&h=400&fit=crop&q=80" },
  ],
  jordan: [
    { title: "Arrival in Amman", desc: "Transfer from Queen Alia airport to your hotel in central Amman (30 minutes). Walk the bustling Rainbow Street lined with cafes, art galleries, and shisha lounges, then enjoy a Jordanian mezze spread of hummus, falafel, fattoush, and slow-roasted mansaf (lamb with yoghurt sauce) at a local restaurant.", schedule: ["Airport transfer", "Hotel check-in", "Rainbow Street evening", "Jordanian dinner"], image: "https://images.unsplash.com/photo-1580834341580-8c17a3a630ca?w=600&h=400&fit=crop&q=80" },
    { title: "Petra", desc: "Drive 3 hours south to Petra and walk through the narrow Siq canyon — a 1.2km gorge with walls soaring 80 metres high — for your first jaw-dropping glimpse of the Treasury carved into rose-red sandstone. Explore the ancient Nabataean city's 800 monuments, climb the 850 steps to the Monastery for desert panoramas, and return for the magical Petra by Night candlelight experience (Mon/Wed/Thu).", schedule: ["Early breakfast", "Drive to Petra", "The Siq entrance", "Treasury reveal", "Explore Petra", "Lunch", "Monastery climb", "Petra by Night"], image: "https://images.unsplash.com/photo-1548786811-dd6e453ccca7?w=600&h=400&fit=crop&q=80" },
    { title: "Wadi Rum", desc: "Drive to Wadi Rum, the 'Valley of the Moon' where Lawrence of Arabia was filmed, and explore the vast red-sand desert by 4x4 past towering sandstone arches, ancient Nabataean inscriptions, and mushroom-shaped rock formations. Share mint tea with Bedouin hosts, watch the sunset turn the desert crimson, and sleep under a canopy of stars at a Bedouin camp.", schedule: ["Breakfast", "Drive to Wadi Rum", "Jeep desert tour", "Bedouin lunch", "Lawrence's Spring", "Sunset in desert", "Bedouin camp dinner"], image: "https://images.unsplash.com/photo-1547234935-80c7145ec969?w=600&h=400&fit=crop&q=80" },
    { title: "Dead Sea", desc: "Drive to the Dead Sea, the lowest point on Earth at 430 metres below sea level, and experience the surreal sensation of floating effortlessly in water ten times saltier than the ocean. Slather yourself in the mineral-rich black mud (a natural spa treatment) and soak in the therapeutic waters before the drive back to Amman for a farewell dinner.", schedule: ["Sunrise in desert", "Drive to Dead Sea", "Float in Dead Sea", "Mud treatment", "Lunch at resort", "Spa relaxation", "Return to Amman", "Farewell dinner"], image: "https://images.unsplash.com/photo-1544551763-8dd44758c2dd?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Enjoy a final Jordanian breakfast of labneh, za'atar flatbread, and fresh mint tea. Pick up Dead Sea mud products, hand-painted ceramics, or sand art bottles from Petra as souvenirs before transferring to the airport.", schedule: ["Final breakfast", "Last views", "Transfer to airport"], image: "https://images.unsplash.com/photo-1548918901-9b31223c5c3a?w=600&h=400&fit=crop&q=80" },
  ],
  croatia: [
    { title: "Arrival & Dubrovnik Walls", desc: "Arrive at Dubrovnik Airport and transfer to the Old Town. Late afternoon walk along the complete circuit of the city walls — the terracotta rooftops, the Adriatic stretching to the horizon, and the harbour below. Sunset drinks at Buža Bar — a cliff bar carved into the rock outside the city walls.", schedule: ["Airport transfer to Old Town", "City walls circuit walk", "Sunset at Buža Bar"], image: "https://images.unsplash.com/photo-1555990793-da11153b2473?w=600&h=400&fit=crop&q=80" },
    { title: "Dubrovnik Old Town", desc: "Morning exploring the Stradun (main street), the Rector's Palace, the Franciscan Monastery with its 14th-century pharmacy, and the Cathedral. Take the cable car to Mount Srđ for panoramic views. Afternoon swimming off the rocks at Banje Beach. Evening Game of Thrones walking tour for fans.", schedule: ["Stradun and Rector's Palace", "Franciscan Monastery", "Mount Srđ cable car", "Banje Beach", "Game of Thrones tour"], image: "https://images.unsplash.com/photo-1512495039889-52a3b799c9bc?w=600&h=400&fit=crop&q=80" },
    { title: "Elaphiti Islands", desc: "Full-day sailing trip to the Elaphiti Islands — three car-free islands just off the coast. Swim in hidden coves, snorkel over underwater walls, explore the botanical gardens of Lokrum Island, and lunch at a family-run konoba (taverna) on Lopud. Return at sunset.", schedule: ["Elaphiti Islands sailing", "Hidden cove swimming", "Lokrum botanical gardens", "Lopud konoba lunch", "Sunset return"], image: "https://images.unsplash.com/photo-1534113414509-0eec2bfb493f?w=600&h=400&fit=crop&q=80" },
    { title: "Split & Diocletian's Palace", desc: "Morning transfer to Split (scenic coastal drive or domestic flight). Explore Diocletian's Palace — a living Roman ruin where 3,000 people still live inside the emperor's retirement home. The Riva waterfront promenade, the Green Market, and the Cathedral bell tower for views. Evening on Bačvice Beach.", schedule: ["Transfer to Split", "Diocletian's Palace", "Riva waterfront", "Cathedral bell tower", "Bačvice Beach"], image: "https://images.unsplash.com/photo-1570522429263-e6e8eaa5b8d2?w=600&h=400&fit=crop&q=80" },
    { title: "Plitvice Lakes or Hvar & Departure", desc: "Option A: Day trip to Plitvice Lakes National Park — boardwalks through 16 cascading turquoise lakes connected by waterfalls. Option B: Ferry to Hvar Island for lavender fields, a 13th-century fortress, and beach clubs. Return to Split for departure.", schedule: ["Plitvice Lakes or Hvar Island", "Turquoise lakes and waterfalls", "Return to Split", "Departure"], image: "https://images.unsplash.com/photo-1555990793-da11153b2473?w=600&h=400&fit=crop&q=80" },
  ],
  "costa rica": [
    { title: "Arrival & San José", desc: "Arrive at Juan Santamaría International Airport and transfer to your hotel. Evening exploring San José's Barrio Escalante — the capital's trendiest neighbourhood, with craft coffee shops and innovative Costa Rican cuisine.", schedule: ["Airport transfer to hotel", "Barrio Escalante evening", "Craft coffee and dinner"], image: "https://images.unsplash.com/photo-1518259102261-b40117eabbc9?w=600&h=400&fit=crop&q=80" },
    { title: "Arenal Volcano & Hot Springs", desc: "Morning drive to La Fortuna. The cone of Arenal Volcano dominates the skyline. Afternoon hike through Arenal's lava trails and hanging bridges through the rainforest canopy. As night falls, soak in volcanic hot springs surrounded by tropical gardens with the volcano as your backdrop.", schedule: ["Drive to La Fortuna", "Arenal lava trails hike", "Hanging bridges", "Volcanic hot springs"], image: "https://images.unsplash.com/photo-1570737543098-0983d88f796d?w=600&h=400&fit=crop&q=80" },
    { title: "Adventure Day", desc: "Morning zip-lining through the cloud forest canopy — a Costa Rican invention. Afternoon whitewater rafting on the Pacuare River, rated one of the top ten rafting rivers in the world. For a gentler option, kayaking on Lake Arenal with volcano views.", schedule: ["Cloud forest zip-lining", "Pacuare River whitewater rafting", "Lake Arenal kayaking option"], image: "https://images.unsplash.com/photo-1559599746-8823b38544c6?w=600&h=400&fit=crop&q=80" },
    { title: "Monteverde Cloud Forest", desc: "Transfer to Monteverde. Walk the hanging bridges and trails of the Monteverde Cloud Forest Reserve — a mystical world of moss-draped trees, orchids, and the elusive resplendent quetzal. Night walk to spot tree frogs, tarantulas, and sleeping birds.", schedule: ["Transfer to Monteverde", "Cloud Forest Reserve trails", "Hanging bridges walk", "Night wildlife walk"], image: "https://images.unsplash.com/photo-1535262412227-85541e910204?w=600&h=400&fit=crop&q=80" },
    { title: "Wildlife & Transfer to Pacific", desc: "Morning at a wildlife sanctuary — spot sloths, toucans, and howler monkeys. Transfer to the Pacific coast — Manuel Antonio area. Afternoon on the beach where the rainforest meets the ocean.", schedule: ["Wildlife sanctuary visit", "Transfer to Pacific coast", "Manuel Antonio beach"], image: "https://images.unsplash.com/photo-1501785888041-af3ef285b470?w=600&h=400&fit=crop&q=80" },
    { title: "Manuel Antonio National Park", desc: "Morning exploring Manuel Antonio National Park — monkeys, iguanas, and sloths along the trails, followed by swimming at pristine jungle-backed beaches. Afternoon catamaran sunset cruise with snorkelling and dolphin spotting. Farewell dinner on the coast.", schedule: ["Manuel Antonio National Park", "Jungle-backed beach swim", "Catamaran sunset cruise", "Farewell coastal dinner"], image: "https://images.unsplash.com/photo-1518259102261-b40117eabbc9?w=600&h=400&fit=crop&q=80" },
    { title: "Beach Morning & Departure", desc: "Final morning on the Pacific. Pura Vida. Transfer to the airport.", schedule: ["Final Pacific morning", "Transfer to airport"], image: "https://images.unsplash.com/photo-1570737543098-0983d88f796d?w=600&h=400&fit=crop&q=80" },
  ],
  peru: [
    { title: "Arrival in Lima", desc: "Arrive at Jorge Chávez International Airport and transfer to your hotel in Miraflores. Evening walk along the clifftop Malecón overlooking the Pacific. Dinner at a top cevichería — Lima is the gastronomic capital of South America.", schedule: ["Airport transfer to Miraflores", "Malecón clifftop walk", "Cevichería dinner"], image: "https://images.unsplash.com/photo-1526392060635-9d6019884377?w=600&h=400&fit=crop&q=80" },
    { title: "Cusco & Acclimatisation", desc: "Morning flight to Cusco at 3,400 metres. Take it easy — coca tea and a gentle walk through the Plaza de Armas. Visit the Qorikancha (Temple of the Sun) where Inca stonework meets Spanish colonial architecture. Evening exploring San Blas artisan quarter and its gallery shops.", schedule: ["Flight to Cusco", "Plaza de Armas and coca tea", "Qorikancha Temple of the Sun", "San Blas artisan quarter"], image: "https://images.unsplash.com/photo-1580619305218-8423a7ef79b4?w=600&h=400&fit=crop&q=80" },
    { title: "Sacred Valley", desc: "Full-day Sacred Valley excursion. Visit the Pisac ruins and the colourful market below. Explore the extraordinary circular Moray terraces — the Incas' agricultural laboratory. Afternoon at the Maras salt mines — thousands of cascading salt pools on a mountainside, harvested since Inca times. Overnight in Ollantaytambo.", schedule: ["Pisac ruins and market", "Moray terraces", "Maras salt mines", "Overnight in Ollantaytambo"], image: "https://images.unsplash.com/photo-1580619305218-8423a7ef79b4?w=600&h=400&fit=crop&q=80" },
    { title: "Machu Picchu", desc: "Early morning train through the Urubamba gorge to Aguas Calientes. Bus up the switchbacks to Machu Picchu — the lost city of the Incas revealed in the morning mist. Guided tour of the Sun Temple, Intihuatana stone, and the residential quarters. Optional hike up Huayna Picchu for aerial views. Return to Cusco.", schedule: ["Train to Aguas Calientes", "Bus to Machu Picchu", "Guided tour of lost city", "Optional Huayna Picchu hike", "Return to Cusco"], image: "https://images.unsplash.com/photo-1587595431973-160d0d94add1?w=600&h=400&fit=crop&q=80" },
    { title: "Rainbow Mountain or Cusco Deep Dive", desc: "Option A: Pre-dawn departure for Rainbow Mountain (Vinicunca) at 5,200 metres — the striped mineral formations in red, gold, green, and turquoise are otherworldly. Option B: Stay in Cusco for a cooking class making ají de gallina and lomo saltado, visit the San Pedro Market, and explore the Sacsayhuamán fortress.", schedule: ["Rainbow Mountain or Cusco exploration", "Vinicunca or cooking class", "San Pedro Market", "Sacsayhuamán fortress"], image: "https://images.unsplash.com/photo-1531968455001-5c5272a67c71?w=600&h=400&fit=crop&q=80" },
    { title: "Lake Titicaca", desc: "Flight or drive to Puno on Lake Titicaca — the highest navigable lake in the world at 3,812 metres. Boat trip to the Uros floating reed islands where families live on totora reed platforms. Continue to Taquile Island for weaving demonstrations and a lunch with lake views.", schedule: ["Flight to Puno", "Lake Titicaca", "Uros floating reed islands", "Taquile Island weaving"], image: "https://images.unsplash.com/photo-1526392060635-9d6019884377?w=600&h=400&fit=crop&q=80" },
    { title: "Return to Lima & Departure", desc: "Flight to Lima. Final afternoon in the Barranco district — street art, bohemian cafés, and the Bridge of Sighs. Transfer to the airport.", schedule: ["Flight to Lima", "Barranco district exploration", "Transfer to airport"], image: "https://images.unsplash.com/photo-1580619305218-8423a7ef79b4?w=600&h=400&fit=crop&q=80" },
  ],
  "new zealand": [
    { title: "Arrival in Auckland", desc: "Transfer from Auckland airport to the city centre (40 minutes) and check into your waterfront hotel. Walk along the Viaduct Harbour lined with restaurants and superyachts, then enjoy fresh New Zealand lamb or green-lipped mussels at a harbour-view restaurant as the Sky Tower lights up the skyline.", schedule: ["Airport transfer", "Hotel check-in", "Harbour evening walk", "Dinner at Viaduct"], image: "https://images.unsplash.com/photo-1507699622108-4be3abd695ad?w=600&h=400&fit=crop&q=80" },
    { title: "Auckland & Rotorua", desc: "Take the lift to the Sky Tower's observation deck (328 metres) for views across Auckland's two harbours, then drive 3 hours south to Rotorua, a geothermal wonderland where the earth bubbles, steams, and smells of sulphur. Experience the powerful Te Puia geysers and a Maori cultural performance with a traditional hangi feast cooked underground.", schedule: ["Breakfast at hotel", "Sky Tower views", "Drive to Rotorua", "Geothermal wonders", "Maori cultural experience", "Hangi dinner"], image: "https://images.unsplash.com/photo-1531804226530-70f8004aa44e?w=600&h=400&fit=crop&q=80" },
    { title: "Hobbiton & Nature", desc: "Visit the Hobbiton Movie Set in Matamata, where 44 hobbit holes are nestled into the rolling green hillside exactly as they appeared in The Lord of the Rings and The Hobbit — end with a ginger beer at the Green Dragon Inn. Continue to the Waitomo Glowworm Caves for a magical boat ride beneath a ceiling of thousands of bioluminescent glowworms.", schedule: ["Breakfast at hotel", "Hobbiton movie set tour", "Lunch at Green Dragon", "Waitomo Glowworm Caves", "Drive to destination", "Scenic dinner"], image: "https://images.unsplash.com/photo-1469521669194-babb45599def?w=600&h=400&fit=crop&q=80" },
    { title: "Queenstown", desc: "Fly to Queenstown, the adventure capital of the world, dramatically set on the shores of Lake Wakatipu beneath the Remarkables mountain range. Ride the Skyline Gondola for panoramic views, choose your adrenaline fix — bungee jumping at Kawarau Bridge (the original site), jet boating through the Shotover Canyon, or a scenic Milford Sound cruise through fiordland.", schedule: ["Flight to Queenstown", "Gondola and views", "Lunch overlooking lake", "Jet boat or bungee", "Milford Sound option", "Fergburger dinner", "Stargazing"], image: "https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=600&h=400&fit=crop&q=80" },
    { title: "Departure", desc: "Grab a final flat white and Fergburger (Queenstown's legendary burger joint) before heading to the airport. Pick up Manuka honey, merino wool, or a greenstone pendant as New Zealand gifts.", schedule: ["Final breakfast", "Last lake views", "Transfer to airport"], image: "https://images.unsplash.com/photo-1469854523086-cc02fe5d8800?w=600&h=400&fit=crop&q=80" },
  ],
  "bora-bora": [
    { title: "Arrival Over the Lagoon", desc: "Fly into Bora Bora's tiny airstrip and board the resort boat transfer across the impossibly turquoise lagoon. Check into your overwater bungalow — glass floor panels reveal reef fish swimming beneath you. First swim in the warm, bathtub-clear water directly from your private deck.", schedule: ["Boat transfer across lagoon", "Overwater bungalow check-in", "First lagoon swim"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Lagoon Snorkelling & Stingray Encounter", desc: "Half-day guided lagoon tour by motorboat. Wade into waist-deep water as blacktip reef sharks and stingrays glide around your legs in their natural habitat. Snorkel the coral gardens teeming with tropical fish. Afternoon at leisure — paddleboard or kayak the lagoon at sunset.", schedule: ["Guided lagoon tour", "Shark and stingray encounter", "Coral garden snorkelling", "Sunset paddleboard or kayak"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Mount Otemanu Hike & 4x4 Tour", desc: "Morning 4x4 island tour exploring Bora Bora's volcanic interior. Visit ancient marae (Polynesian temples), WWII gun emplacements, and lush hillside lookouts with lagoon views. Afternoon free for relaxation. Sunset dinner on the overwater restaurant deck.", schedule: ["4x4 island tour", "Ancient marae temples", "WWII gun emplacements", "Overwater restaurant dinner"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Private Motu Picnic", desc: "Boat transfer to a private motu (small island) for a champagne picnic on an empty white sand beach. Snorkel the surrounding reef at your own pace. Complete seclusion — just you, the sand, and the Pacific. Return to the resort for a couples' Polynesian massage.", schedule: ["Private motu boat transfer", "Champagne beach picnic", "Reef snorkelling", "Polynesian couples massage"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Diving or Deep-Sea Fishing", desc: "Full-day choice: scuba diving at one of Bora Bora's world-class dive sites (manta ray channel, shark feeding), or a deep-sea fishing charter targeting mahi-mahi and tuna. Non-adventurers enjoy the resort's infinity pool. Evening Polynesian dance performance and fire show.", schedule: ["Scuba diving or deep-sea fishing", "Resort infinity pool", "Polynesian dance and fire show"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Jet Ski Lagoon Circuit", desc: "Morning jet ski tour around the entire lagoon — stopping at sandbars and viewpoints with Mount Otemanu towering above. Afternoon spa treatment. Final sunset from the resort's overwater bar, cocktail in hand.", schedule: ["Jet ski lagoon circuit", "Sandbars and viewpoints", "Spa treatment", "Final overwater bar sunset"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Sunrise Paddleboard & Departure", desc: "Early morning stand-up paddleboard session on the glassy lagoon as the sun rises over the reef. Leisurely final breakfast. Boat transfer to the airport for departure.", schedule: ["Sunrise paddleboard", "Final breakfast", "Boat transfer to airport"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
  ],
  mykonos: [
    { title: "Arrival & Little Venice Sunset", desc: "Arrive at Mykonos Airport and transfer to your hotel. Evening walk through Mykonos Town's whitewashed labyrinth — boutiques, galleries, and the famous Paraportiani Church. Secure a waterfront table in Little Venice as the sun drops into the Aegean behind the iconic windmills. Dinner at a candlelit taverna.", schedule: ["Airport transfer to hotel", "Mykonos Town exploration", "Little Venice sunset", "Candlelit taverna dinner"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Delos Island Excursion", desc: "Morning boat trip to Delos — the sacred island birthplace of Apollo, now an open-air archaeological museum. Walk among 2,500-year-old temples, mosaics, and the iconic Terrace of the Lions. Return to Mykonos for an afternoon on Ornos Beach. Evening cocktails in Mykonos Town.", schedule: ["Delos Island boat trip", "Ancient temples and mosaics", "Terrace of the Lions", "Ornos Beach afternoon", "Mykonos Town cocktails"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Beach Hopping", desc: "Day of Mykonos's famous beaches. Start at Paradise Beach for the morning scene, move to Super Paradise for a swim, and end at the quieter Agios Sostis — a locals' favourite with no sunbeds, just golden sand. Sunset from the hilltop above the windmills.", schedule: ["Paradise Beach", "Super Paradise Beach", "Agios Sostis Beach", "Windmill hilltop sunset"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Food & Wine", desc: "Morning cooking class at a Mykonian farm — learn to make traditional kopanisti cheese spread and louza (cured pork). Vineyard visit and wine tasting at a local estate. Afternoon exploring the back streets of Ano Mera village and the Panagia Tourliani monastery. Evening at a fine-dining restaurant with Aegean views.", schedule: ["Mykonian farm cooking class", "Vineyard wine tasting", "Ano Mera village", "Fine-dining Aegean dinner"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Windmills & Departure", desc: "Final morning exploring the town — the folklore museum, the maritime museum, and a last espresso freddo overlooking the harbour. Transfer to the airport.", schedule: ["Folklore and maritime museums", "Final espresso freddo", "Transfer to airport"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
  ],
  "amalfi-coast": [
    { title: "Arrival in Positano", desc: "Arrive at Naples Airport and transfer along the winding coast road — your first glimpse of the pastel villages tumbling down to the sea. Check into your cliffside hotel in Positano. Evening passeggiata through the village's steep lanes, ending at the beach for dinner with your feet almost in the Mediterranean.", schedule: ["Naples Airport transfer", "Amalfi Coast drive", "Positano cliffside hotel", "Evening passeggiata and beach dinner"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Amalfi & Ravello", desc: "Morning boat to Amalfi town. Explore the 9th-century Cathedral with its striped façade and the Cloister of Paradise. Continue by bus up the hairpin roads to Ravello — the mountaintop town beloved by Wagner and Virginia Woolf. Visit Villa Rufolo's gardens suspended over the coast. Lunch at a terrace restaurant with views that stretch to infinity.", schedule: ["Boat to Amalfi town", "Amalfi Cathedral", "Cloister of Paradise", "Ravello", "Villa Rufolo gardens", "Terrace restaurant lunch"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Path of the Gods", desc: "Hike the Sentiero degli Dei (Path of the Gods) from Agerola to Nocelle — a spectacular clifftop trail 500 metres above the sea with views of Capri on clear days. Descend the 1,700 steps to Positano and reward yourself with a cold limoncello on the beach. Evening at leisure.", schedule: ["Sentiero degli Dei hike", "Clifftop trail with Capri views", "1,700 steps descent to Positano", "Beach limoncello", "Evening at leisure"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Capri Day Trip", desc: "Ferry to the island of Capri. Ride the funicular to Capri Town for boutique shopping and people-watching at the Piazzetta. Visit the Gardens of Augustus for views of the Faraglioni rocks. Optional Blue Grotto visit (weather permitting). Lunch at a lemon-grove restaurant. Return to the Amalfi Coast for a farewell seafood dinner.", schedule: ["Ferry to Capri", "Capri Town funicular", "Gardens of Augustus", "Blue Grotto", "Lemon-grove restaurant", "Farewell seafood dinner"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Limoncello & Departure", desc: "Morning visit to a lemon grove for a limoncello-making demonstration — taste the difference when it's made with Amalfi's famous sfusato lemons. Last espresso overlooking the coast. Transfer to Naples Airport.", schedule: ["Lemon grove limoncello demo", "Final coastal espresso", "Transfer to Naples Airport"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
  ],
  seychelles: [
    { title: "Arrival on Mahé", desc: "Arrive at Seychelles International on Mahé and transfer to your resort. Afternoon on Beau Vallon Beach — the island's most popular stretch of sand with calm turquoise water. First Creole dinner with grilled fish and tropical fruit.", schedule: ["Airport transfer to resort", "Beau Vallon Beach", "First Creole dinner"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Mahé Nature & Beaches", desc: "Morning hike in the Morne Seychellois National Park through misty cloud forest. Afternoon beach-hopping: Anse Intendance's dramatic surf beach, the hidden cove of Anse Major (accessible only by trail or boat). Sunset from the hilltop Mission Lodge viewpoint.", schedule: ["Morne Seychellois hike", "Anse Intendance beach", "Anse Major cove", "Mission Lodge sunset"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Praslin & Vallée de Mai", desc: "Inter-island flight or ferry to Praslin. Visit the Vallée de Mai — a UNESCO World Heritage forest of ancient Coco de Mer palms (the world's largest seed). Afternoon at Anse Lazio, consistently voted one of the world's best beaches — granite boulders, powder sand, crystalline water.", schedule: ["Inter-island transfer to Praslin", "Vallée de Mai UNESCO forest", "Anse Lazio beach"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "La Digue by Bicycle", desc: "Ferry to La Digue — a tiny island with no cars, just bicycles and ox carts. Cycle to Anse Source d'Argent — the most photographed beach on earth, with giant granite boulders and shallow turquoise water. Explore the colonial L'Union Estate with its copra mill and giant tortoises. Picnic lunch on a secluded beach.", schedule: ["Ferry to La Digue", "Cycle to Anse Source d'Argent", "L'Union Estate and giant tortoises", "Secluded beach picnic"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Snorkelling & Marine Park", desc: "Full-day snorkelling excursion to Sainte Anne Marine National Park or the Cocos Island reserve. Swim with hawksbill turtles, parrotfish, and rays in protected waters. Glass-bottom boat for non-swimmers. Return to Praslin for a sunset seafood barbecue.", schedule: ["Marine park snorkelling", "Hawksbill turtle swimming", "Glass-bottom boat", "Sunset seafood barbecue"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Island Hopping & Creole Culture", desc: "Visit Curieuse Island — walk among free-roaming giant Aldabra tortoises in a mangrove nature reserve. Creole lunch prepared by park rangers. Afternoon exploring Praslin's Côte d'Or beach. Evening cooking class making Creole curry with coconut milk, breadfruit, and fresh-caught fish.", schedule: ["Curieuse Island tortoises", "Creole ranger lunch", "Côte d'Or beach", "Creole cooking class"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Final Swim & Departure", desc: "Leisurely morning at the resort. Last swim in the Indian Ocean. Transfer to the airport for departure.", schedule: ["Leisurely resort morning", "Final Indian Ocean swim", "Transfer to airport"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
  ],
  fes: [
    { title: "Arrival & Riad Welcome", desc: "Arrive at Fes-Saïs Airport and transfer to your riad in the heart of the medina. Navigate the narrow alleys — your first taste of the world's largest car-free urban zone. Evening on the riad's rooftop terrace watching the minaret lights flicker to life. Dinner of pastilla (pigeon pie) and lamb tagine.", schedule: ["Airport transfer to riad", "Medina first exploration", "Rooftop terrace evening", "Pastilla and tagine dinner"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Fes el-Bali Medina", desc: "Full-day guided exploration of Fes el-Bali — the medieval medina. Visit the Chouara tannery from the leather shops above (the circular stone vats of colourful dye are unforgettable). Explore the Al-Qarawiyyin Mosque complex — the world's oldest continuously operating university, founded in 859 AD. The Bou Inania Madrasa for its stucco carving and zellige tilework. Lunch at a traditional palace restaurant.", schedule: ["Chouara tannery", "Al-Qarawiyyin Mosque", "Bou Inania Madrasa", "Traditional palace lunch"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Chefchaouen Day Trip", desc: "Full-day excursion to Chefchaouen — Morocco's famous blue city in the Rif Mountains. Every wall, stairway, and doorway is washed in shades of azure and cobalt. Wander the photogenic alleys, visit the kasbah, and lunch with mountain views. Return to Fes.", schedule: ["Chefchaouen excursion", "Blue city alleys", "Kasbah visit", "Mountain view lunch"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Desert Excursion or Meknès & Volubilis", desc: "Option A: Drive south into the Middle Atlas — visit Ifrane (the 'Morocco Switzerland'), the cedar forests with Barbary macaques, and the gateway to the Sahara. Option B: Day trip to the imperial city of Meknès and the Roman ruins of Volubilis — remarkably preserved mosaics and triumphal arch. Evening hammam and massage in Fes.", schedule: ["Middle Atlas or Meknès excursion", "Ifrane or Volubilis ruins", "Evening hammam and massage"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Artisan Quarter & Departure", desc: "Morning visiting Fes's artisan cooperatives — watch brass-workers, potters, and weavers at work, and shop for authentic handcrafted souvenirs. Last mint tea on the rooftop. Transfer to the airport.", schedule: ["Artisan cooperatives", "Brass, pottery, weaving", "Final rooftop mint tea", "Transfer to airport"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
  ],
  fiji: [
    { title: "Arrival & Bula Welcome", desc: "Arrive at Nadi International Airport and transfer to your island resort by boat or seaplane. The traditional Fijian welcome — garlands, a shell horn blown to announce your arrival, and your first bowl of kava (the mild ceremonial drink). Settle in to island time.", schedule: ["Airport transfer by boat or seaplane", "Traditional Fijian welcome", "First kava ceremony"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Reef Snorkelling", desc: "Morning guided snorkelling on the house reef — Fiji's coral is among the most pristine in the world, earning it the title 'Soft Coral Capital.' Tropical fish, sea fans, and giant clams in impossibly clear water. Afternoon at leisure — hammock, beach, infinity pool. Sunset lovo feast (underground earth oven) with meke (traditional Fijian dance).", schedule: ["House reef snorkelling", "Tropical fish and coral", "Afternoon at leisure", "Sunset lovo feast and meke dance"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Village Visit", desc: "Morning boat trip to a local Fijian village. Participate in a kava ceremony with the chief, visit the school, and learn about the communal way of life. Villagers may demonstrate traditional fishing, weaving, and cooking methods. Afternoon swimming at a waterfall accessible only by boat.", schedule: ["Fijian village boat trip", "Kava ceremony with chief", "Village school visit", "Waterfall swimming"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Island Hopping", desc: "Full-day island-hopping excursion. Visit two or three uninhabited islands — each a different shade of blue. Picnic lunch on a sandbar that only appears at low tide. Snorkelling, paddleboarding, and complete seclusion.", schedule: ["Island-hopping excursion", "Uninhabited island visits", "Sandbar picnic lunch", "Snorkelling and paddleboarding"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Diving or Surfing", desc: "Full-day choice: scuba diving at the Great Astrolabe Reef or Beqa Lagoon (shark diving for the adventurous), or a surfing trip to Cloudbreak — one of the world's most famous reef breaks. Non-adventurers enjoy a Fijian spa treatment using coconut and tropical oils.", schedule: ["Scuba diving or surfing", "Great Astrolabe Reef or Cloudbreak", "Fijian spa treatment option"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "River & Rainforest", desc: "Morning bamboo rafting along a jungle river — float through the rainforest canopy. Afternoon waterfall hike to a freshwater swimming hole. Return to the resort for a final sunset — Fijian sunsets are legendary.", schedule: ["Bamboo river rafting", "Waterfall hike", "Freshwater swimming hole", "Final Fijian sunset"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Final Swim & Departure", desc: "Leisurely morning on the beach. Last snorkel. Transfer to the airport with a shell necklace and a 'Bula vinaka.'", schedule: ["Final beach morning", "Last snorkel", "Transfer to airport"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
  ],
  kenya: [
    { title: "Arrival in Nairobi", desc: "Arrive at Jomo Kenyatta International Airport and transfer to your hotel. Afternoon visit to the David Sheldrick Wildlife Trust — watch orphaned baby elephants being bottle-fed and mud-bathed. Evening dinner at a Nairobi restaurant with views of the national park.", schedule: ["Airport transfer to hotel", "David Sheldrick Wildlife Trust", "Nairobi dinner with park views"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Masai Mara — First Safari", desc: "Morning flight to the Masai Mara. Check into your safari lodge or luxury tented camp. Afternoon game drive — the Mara's open grasslands offer unmatched visibility. Spot lions, elephants, buffalo, zebra, and giraffe. Sundowner cocktails on the savannah as the sky turns amber.", schedule: ["Flight to Masai Mara", "Safari lodge check-in", "Afternoon game drive", "Savannah sundowner cocktails"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Dawn Drive & Maasai Village", desc: "Pre-dawn game drive — the best time for predator sightings. Watch cheetahs begin their hunt and hyenas returning to their dens. After breakfast, visit a Maasai village — meet the warriors, learn about their semi-nomadic lifestyle, and see the traditional jumping dance. Afternoon at leisure at the camp.", schedule: ["Pre-dawn predator game drive", "Maasai village visit", "Traditional jumping dance", "Camp leisure afternoon"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Hot Air Balloon Safari", desc: "Pre-dawn hot air balloon flight over the Mara — drift silently above the plains as the sun rises and animals begin to stir below. Land for a champagne bush breakfast. Afternoon game drive focusing on the Mara River — during migration season (July-October), this is where the dramatic crossings happen.", schedule: ["Hot air balloon flight", "Champagne bush breakfast", "Mara River game drive", "Migration crossing viewing"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Full Day in the Mara", desc: "Full-day game drive with picnic lunch in the bush. Track the Big Five and search for leopards in the riverine forest. Visit a hippo pool. The Mara's birdlife is extraordinary — over 450 species. Evening bush dinner under the stars.", schedule: ["Full-day Big Five game drive", "Bush picnic lunch", "Hippo pool visit", "Bush dinner under stars"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Lake Nakuru or Lake Naivasha", desc: "Fly or drive to Lake Nakuru National Park — flamingos lining the lakeshore in pink, rhinos in the woodland, and Rothschild giraffes. Alternative: Lake Naivasha for a boat ride among hippos and fish eagles, then cycling through Hell's Gate National Park with its dramatic canyon. Overnight near the lake.", schedule: ["Lake Nakuru or Naivasha", "Flamingos and rhinos", "Boat ride or cycling", "Overnight near lake"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Nairobi & Departure", desc: "Return to Nairobi. Morning visit to the Giraffe Centre — hand-feed endangered Rothschild giraffes from an elevated platform. Browse the Kazuri Beads factory or Maasai Market for souvenirs. Transfer to the airport.", schedule: ["Return to Nairobi", "Giraffe Centre", "Maasai Market souvenirs", "Transfer to airport"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
  ],
  tenerife: [
    { title: "Arrival & Beach", desc: "Arrive at Tenerife South Airport and transfer to your resort. Afternoon on the beach — Playa de las Américas or the golden sand of Playa del Duque. First Canarian dinner with papas arrugadas (wrinkled potatoes) and mojo sauces.", schedule: ["Airport transfer to resort", "Beach afternoon", "Canarian dinner"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Mount Teide National Park", desc: "Full-day excursion to Spain's highest peak — Mount Teide at 3,718 metres. Drive through dramatic volcanic landscapes of lava fields and rock formations. Take the cable car to the upper station for views across the entire Canary Islands archipelago. Walk the lunar landscape of the caldera floor. Stargazing experience in the evening — Teide is one of the world's best astronomical observation sites.", schedule: ["Mount Teide excursion", "Volcanic landscapes drive", "Cable car to upper station", "Caldera floor walk", "Evening stargazing"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Anaga Mountains & La Laguna", desc: "Explore the ancient laurel forests of the Anaga Rural Park — misty trails through primeval woodland. Lunch in a guachinche (informal local restaurant) in the mountain villages. Afternoon in San Cristóbal de La Laguna — the UNESCO-listed colonial town with colourful streets and the island's oldest university.", schedule: ["Anaga Rural Park laurel forests", "Guachinche mountain lunch", "La Laguna UNESCO town"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Los Gigantes & Whale Watching", desc: "Morning boat trip along the western coast. Spot resident pods of pilot whales and bottlenose dolphins in the channel between Tenerife and La Gomera. Continue to the Los Gigantes cliffs — 600-metre vertical walls plunging into the Atlantic. Afternoon at Masca village, perched in a dramatic gorge.", schedule: ["Western coast boat trip", "Whale and dolphin watching", "Los Gigantes cliffs", "Masca village"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Siam Park & Leisure", desc: "Morning at Siam Park — voted the world's best water park, with record-breaking slides and a Thai-themed setting. Afternoon at leisure — beach, pool, or shopping in Costa Adeje. Evening tapas crawl.", schedule: ["Siam Park water park", "Afternoon at leisure", "Evening tapas crawl"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Puerto de la Cruz & North Coast", desc: "Day trip to the greener north coast. Explore Puerto de la Cruz — the Lago Martiánez sea pools designed by César Manrique, the botanical gardens with 3,000 tropical species, and the atmospheric old town. Visit the Drago Milenario in Icod de los Vinos — a thousand-year-old dragon tree.", schedule: ["Puerto de la Cruz", "Lago Martiánez sea pools", "Botanical gardens", "Drago Milenario dragon tree"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Final Beach Day & Departure", desc: "Morning swimming or a last walk along the promenade. Transfer to the airport.", schedule: ["Final beach morning", "Transfer to airport"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
  ],
  tuscany: [
    { title: "Arrival & Florence", desc: "Arrive at Florence Airport and transfer to your agriturismo or hotel. If arriving early, evening walk through Florence — the Ponte Vecchio at sunset, the Duomo lit up against the sky, and dinner at a trattoria in the Oltrarno neighbourhood.", schedule: ["Airport transfer", "Florence evening walk", "Ponte Vecchio sunset", "Oltrarno trattoria dinner"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Florence Art & Architecture", desc: "Morning at the Uffizi Gallery — Botticelli's Birth of Venus, da Vinci, Raphael. Climb the Duomo's 463 steps for a panoramic reward. Afternoon strolling the Boboli Gardens and crossing the Ponte Vecchio's goldsmith shops. Sunset at Piazzale Michelangelo — the most famous viewpoint in Florence, with the entire city laid out below.", schedule: ["Uffizi Gallery", "Duomo climb", "Boboli Gardens", "Piazzale Michelangelo sunset"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Chianti Wine Country", desc: "Full-day drive through Chianti's rolling hills. Visit three boutique wineries — taste Chianti Classico, Brunello, and Super Tuscans among the vines. Gourmet lunch at a vineyard restaurant with homemade pici pasta and wild boar ragù. Stop at the medieval village of Greve in Chianti. Return through cypress-lined roads at golden hour.", schedule: ["Chianti wine country drive", "Three boutique wineries", "Vineyard restaurant lunch", "Greve in Chianti village"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Siena & San Gimignano", desc: "Morning in Siena — the shell-shaped Piazza del Campo (home of the Palio horse race), the striped Cathedral, and the views from Torre del Mangia. Continue to San Gimignano, the medieval 'Manhattan of Tuscany' with its 14 surviving tower-houses. Try the world-champion gelato at Gelateria Dondoli.", schedule: ["Siena Piazza del Campo", "Siena Cathedral", "San Gimignano towers", "Gelateria Dondoli gelato"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Val d'Orcia & Departure", desc: "Morning drive through the Val d'Orcia — the iconic Tuscan landscape of lone cypress trees, golden fields, and hilltop villages. Stop at Pienza for pecorino cheese and Montepulciano for Vino Nobile. Final espresso overlooking the landscape. Transfer to the airport.", schedule: ["Val d'Orcia drive", "Pienza pecorino cheese", "Montepulciano Vino Nobile", "Transfer to airport"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
  ],
  "rio-de-janeiro": [
    { title: "Arrival & Copacabana", desc: "Arrive at Galeão International Airport and transfer to your hotel. Evening walk along Copacabana Beach's famous mosaic promenade — the energy of volleyball games, capoeira circles, and sunset vendors. First caipirinha at a beachfront kiosk.", schedule: ["Airport transfer to hotel", "Copacabana Beach promenade", "First caipirinha"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Christ the Redeemer & Corcovado", desc: "Morning cog train up Corcovado Mountain through the Tijuca rainforest to Christ the Redeemer — arms outstretched 710 metres above sea level with a 360-degree panorama of the city, the bay, Sugarloaf, and the beaches. Afternoon in the bohemian neighbourhood of Santa Teresa — cobblestone streets, art studios, and the colourful Selarón Steps.", schedule: ["Corcovado cog train", "Christ the Redeemer", "Santa Teresa neighbourhood", "Selarón Steps"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Sugarloaf & Botafogo", desc: "Morning cable car to the summit of Sugarloaf Mountain — two stages with spectacular bay views from each platform. Afternoon on Botafogo Beach watching the sunset paint Sugarloaf in gold and pink. Evening at a samba club in Lapa — live music, dancing, and Rio's legendary nightlife.", schedule: ["Sugarloaf cable car", "Bay views", "Botafogo Beach sunset", "Lapa samba club"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Tijuca Forest & Ipanema", desc: "Morning hike in Tijuca National Park — the world's largest urban rainforest, with waterfalls, lookout points, and wildlife. Afternoon at Ipanema Beach — the girl from Ipanema's home turf. Beach football, people-watching, and coconut water from the vendors. Evening at the Feira de São Cristóvão for northeastern Brazilian food and forró music.", schedule: ["Tijuca National Park hike", "Ipanema Beach", "Feira de São Cristóvão"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Favela Tour & Street Art", desc: "Morning guided community tour of a favela — meet residents, see community projects, and gain perspective on Rio's social complexity. Afternoon exploring the street art of the port zone — the massive Olympic Boulevard murals. Visit the Museum of Tomorrow for its futuristic architecture and sustainability exhibits.", schedule: ["Favela community tour", "Port zone street art", "Museum of Tomorrow"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Ilha Grande Day Trip", desc: "Full-day boat trip to Ilha Grande — a tropical island paradise with no cars and pristine beaches. Snorkel in the Blue Lagoon, swim at Lopes Mendes Beach (one of Brazil's best), and hike through Atlantic Forest. Return to Rio.", schedule: ["Ilha Grande boat trip", "Blue Lagoon snorkelling", "Lopes Mendes Beach", "Atlantic Forest hike"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Maracanã & Departure", desc: "Morning tour of the Maracanã Stadium — the cathedral of world football. Last açaí bowl on the beach. Transfer to the airport.", schedule: ["Maracanã Stadium tour", "Final açaí bowl", "Transfer to airport"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
  ],
  "swiss-alps": [
    { title: "Arrival & Lucerne", desc: "Arrive at Zurich Airport and transfer to Lucerne. Afternoon walk across the medieval Chapel Bridge (the oldest covered wooden bridge in Europe), stroll the lakefront, and take in the mountain panorama. Dinner in the atmospheric Old Town.", schedule: ["Zurich Airport transfer to Lucerne", "Chapel Bridge walk", "Lakefront stroll", "Old Town dinner"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Jungfrau Region", desc: "Full-day excursion to the Jungfrau region. Train to Grindelwald, then up to Jungfraujoch — the 'Top of Europe' at 3,454 metres. Walk through the ice palace carved inside the glacier, step onto the observation terrace for views of the Aletsch Glacier, and take in air that feels like the top of the world. Return via the scenic mountain railway through Lauterbrunnen valley.", schedule: ["Train to Grindelwald", "Jungfraujoch Top of Europe", "Ice palace and Aletsch Glacier", "Lauterbrunnen valley return"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Zermatt & The Matterhorn", desc: "Train to Zermatt — the car-free village at the foot of the Matterhorn. Ride the Gornergrat cogwheel railway for the iconic Matterhorn reflection in the Riffelsee lake. Hike the Five Lakes Trail with mountain views at every turn. Lunch at a mountain hut restaurant with cheese fondue.", schedule: ["Train to Zermatt", "Gornergrat railway", "Matterhorn Riffelsee reflection", "Five Lakes Trail hike", "Mountain hut fondue"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Glacier Express", desc: "Board the legendary Glacier Express — the 'slowest express train in the world' — through 91 tunnels, over 291 bridges, and across the Oberalp Pass. Wind through the Rhine Gorge (the Swiss Grand Canyon), alpine meadows, and pristine valleys. The panoramic windows make every moment postcard-perfect.", schedule: ["Glacier Express journey", "91 tunnels and 291 bridges", "Rhine Gorge", "Panoramic alpine views"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Lake Cruise & Departure", desc: "Morning paddlesteamer cruise on Lake Lucerne — surrounded by mountains on all sides. Visit the Swiss Transport Museum or the Lion Monument. Final Swiss chocolate from a master chocolatier. Transfer to the airport.", schedule: ["Lake Lucerne paddlesteamer cruise", "Swiss Transport Museum", "Final Swiss chocolate", "Transfer to airport"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
  ],
  oman: [
    { title: "Arrival in Muscat", desc: "Arrive at Muscat International Airport and transfer to your hotel. Evening walk along the Mutrah Corniche — the picturesque harbour promenade with traditional dhows, the old watchtowers, and the Mutrah Souk glowing with copper lanterns and frankincense smoke.", schedule: ["Airport transfer to hotel", "Mutrah Corniche walk", "Mutrah Souk evening"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Grand Mosque & Royal Opera", desc: "Morning at the Sultan Qaboos Grand Mosque — one of the most beautiful mosques in the world, with the second-largest hand-woven carpet (4,343 square metres) and a stunning Swarovski crystal chandelier. Visit the National Museum for Oman's maritime and cultural heritage. Afternoon in the Royal Opera House — even the exterior is breathtaking. Evening at a traditional Omani restaurant for shuwa (slow-cooked lamb).", schedule: ["Sultan Qaboos Grand Mosque", "National Museum", "Royal Opera House", "Traditional Omani dinner"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Wadis & Coastal Beauty", desc: "Full-day excursion to the eastern wadis. Swim in the emerald pools of Wadi Shab — a canyon where the turquoise water is reached by hiking and scrambling through a narrow gorge. Continue to the Bimmah Sinkhole for a swim in its turquoise pool. Afternoon at the white sand beaches of Fins or Tiwi.", schedule: ["Wadi Shab emerald pools", "Bimmah Sinkhole", "Fins or Tiwi beaches"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Jebel Akhdar & Nizwa Fort", desc: "Drive into the Al Hajar Mountains to Jebel Akhdar — the 'Green Mountain' with its terraced rose gardens, pomegranate orchards, and mountain villages at 2,000 metres. Continue to Nizwa — Oman's former capital. Explore the 17th-century Nizwa Fort and the traditional souk famous for silver jewellery, khanjars (curved daggers), and pottery. Friday goat market if timing aligns.", schedule: ["Jebel Akhdar Green Mountain", "Terraced rose gardens", "Nizwa Fort", "Traditional souk"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
    { title: "Desert or Diving & Departure", desc: "Morning option: Drive to the edge of the Wahiba Sands desert for a short dune experience and Bedouin coffee. Or head to the Daymaniyat Islands for world-class snorkelling with sea turtles and whale sharks (seasonal). Return to Muscat and transfer to the airport.", schedule: ["Wahiba Sands or Daymaniyat Islands", "Desert dunes or snorkelling", "Return to Muscat", "Transfer to airport"], image: "https://images.unsplash.com/photo-1500000000000-000000000000?w=600&h=400&fit=crop&q=80" },
  ],
};
